INSERT INTO `course_infos` (`id`, `coursename`, `firstletter`, `coursedetails`, `certification`, `whothiscoursefor`, `learning`, `price`, `currency_name`, `instructor`, `rating`, `duration`, `lesson`, `quizzes`, `certificate`, `language`, `access`, `created_at`, `updated_at`, `picture`, `MainHead`) VALUES
(5, 'Enterprise Risk Management', 'e', '<div>\n<p><strong>Course Outline</strong></p>\n<p>Day One</p>\n<p>Introduction to risk management\nHow to define risk?\nWhat covers the risk management process? \nWhy manage risk?\nThe attributes of effective risk management that will fit your organisation\nKey risk management disciplines\nThe risk management process\nIdentify the proper communication channels and the stakeholders\nEstablish the internal and external contexts\nRisk assessment process\nRisk identification and analysis tools and techniques that will fit your needs</p>\n<p>Day Two</p>\n<p>The risk management process\nRisk prioritisation: define the likelihood and impacts\nBuild your relevant risk heatmap\nDesign a meaningful risk register\nDefine your organisation’s risk appetite and tolerance levels\nHow to treat the risks?\nMonitoring and review: keep constantly updated and relevant views on your exposures\nThe risk management framework and embedding a risk culture\nRisk management framework\nEmbedding risk management in your corporate culture\nBeing a successful risk manager: know your strengths\nBenefits of risk management: how to increase your performance</p>\n</div>', NULL, '<div>\n<p><strong>Who should take this course</strong></p>\n<p>Risk professionals</p>\n<p>Business leaders (CEOs, heads of function,senior managers)</p>\n<p>Assurance providers, e.g., internal and external\nauditors</p>\n<p> Non-executive directors (particularly those\nwith responsibility for audit and risk)</p>\n<p>Professional advisors wishing to provide more\nexpert support for their clients’ market</p>\n</div>', '<p><br></p>', 120000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'Lifetime', '2022-05-12 02:50:57', '2022-05-12 02:50:57', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1652449131/Tmc%20institute/Enterprise-risk-management_ogbrgc.png', 'Risk Management Courses'),
(6, 'Effective Risk Registers and Assessments', 'e', '<p>A good risk register informs corporate decisionmaking, and allows an organisation to show </p><p>how it is identifying and managing threats to its objectives effectively.</p><p>This course gives you practical tools and techniques to create your own risk management toolkit. </p><p>Find out how to engage the interest of all stakeholders to capture valuable information from them, </p><p>and how to inform decision makers of the greatest threats they face</p>', NULL, '<ul className=\"w-full\">\n<li><strong>Who should take this course</strong></li>\n<li>Risk Managers.</li>\n<li>Risk Champions.</li>\n<li>Risk facilitators.</li>\n<li>Internal auditors.</li>\n<li>Health and Safety officers.</li>\n<li>IT risk managers.</li></ul>', '<ul className=\"w-full\">\n<li><strong>Learn about</strong></li>\n<li>Designing a fit-for-purpose risk register.</li>\n<li>Ensuring your register is the go-to dashboard for status reports.</li>\n<li>Analysing business objectives and defining the scope of risk assessments.</li>\n<li>Identifying the most appropriate risk assessment technique.</li>\n<li>Planning and facilitating risk assessments.</li>\n<li>Evaluating risks based on their rating and your organisation’s risk tolerance.</li>\n</ul>', 120000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 02:56:19', '2022-05-12 02:56:19', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1652449131/Tmc%20institute/Effective-Risk-Registers-and-Assessments_jpptlq.png', 'Risk Management Courses'),
(7, 'Risk Assessment  Essentials & Techniques', 'r', '<p><strong>Course Outline</strong></p>\n<p>Day One</p>\n<ul>\n<li>Overview and selection of risk assessment techniques</li>\n<li>Risk identification techniques: Brainstorming and Interviews, </li>\n<li>Risk identification techniques: Check-lists and Primary hazard analysis</li>\n<li>Risk identification techniques: Hazard and operability studies (HAZOP)\n</li></ul><p><br></p><p>Day Two</p><ul>\n<li>Risk analysis techniques: Structure “What if?” (SWIFT), Scenario analysis</li><li>Risk analysis techniques: Business impact analysis, Root cause analysis</li>\n<li>Risk assessment techniques: Failure mode effect analysis, Fault tree analysis,</li>\n<li>Event tree analysis</li>\n<li>Risk assessment techniques: Bow tie analysis, Multi-criteria-decision analysis (MCDA)</li></ul>\n', NULL, '<ul className=\"w-full\">\n<li><strong>Who should take this course</strong></li>\n<li>Risk Managers.</li>\n<li>Risk Champions.</li>\n<li>Risk Facilitators.</li>\n<li>Internal Auditors.</li>\n<li>Health and Safety officers.</li>\n<li>IT Risk Managers.</li>\n</ul>\n', '<p><br></p>', 120000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 03:00:46', '2022-05-12 03:00:46', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1652449129/Tmc%20institute/Risk-Assessment-Essentials-_-Techniques_s702cd.png', 'Risk Management Courses'),
(8, 'Best Practices in Operational Risk Management', 'b', '<ul className=\"w-full\">\n<li><strong>Course Outline</strong></li>\n<li>Session 1 - Day One: Operational Risk Framework and Governance</li><li>Session 2 - Day One: Risk Identification &amp; RCSA</li>\n<li>Session 3 - Day One: Root causes analysis and Control Design</li>\n<li>Session 4 - Day One: Designing and Selecting Preventive Key Risk Indicators</li>\n<li>Session 1 - Day Two: Risk Appetite Definition, Statement, and Communication</li>\n<li>Session 2 - Day Two: Reporting and Escalation</li>\n<li>Session 3 - Day Two: Scenario Analysis: Application to Cyber risk</li>\n<li>Session 4 - Day Two: Risk Culture and Conduct</li></ul>\n', NULL, '<ul>\n<li><strong>Who should take this course</strong></li>\n<li>Risk Managers.</li>\n<li>Risk Champions.</li>\n<li>Risk facilitators.</li>\n<li>Internal auditors.</li>\n<li>Health and Safety officers.</li>\n<li>IT risk managers.</li></ul>', '<p><br></p>', 120000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 03:04:02', '2022-05-12 03:04:02', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1652449131/Tmc%20institute/Best-Practices-in-Operational-Risk-Management_e5aczc.png', 'Risk Management Courses'),
(9, 'Operational Risk for Auditors and Supervisors', 'o', '<ul className=\"w-full\">\n<li><strong>Course Outline</strong></li>\n<li>Session 1: Risk Appetite & consistency of an Operational Risk Framework</li>\n<li>Session 2: Maturity Criteria in Operational Risk Management</li>\n<li>Session 3: Documentation vs Culture</li>\n<li>Session 4: Risk-based allocation of resources: a general approach</li>\n</ul>', NULL, '<p><br></p>', '<p><br></p>', 100000.00, 'naira', NULL, NULL, '1 day', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 03:06:08', '2022-05-12 03:06:08', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1652449131/Tmc%20institute/Operational-Risk-for-Auditors-and-Supervisors_thws5d.png', 'Risk Management Courses'),
(10, 'Essentials of Risk Management', 'e', '<ul className=\"w-full\">\n<li><strong>Course Modules</strong></li>\n<li>Module 1: Elements of Risk</li>\n<li>Module 2: Risk Evaluation</li><li>Module 3: Risk Mitigation</li>\n<li>Module 4: The value of risk management</li>\n</ul>\n<p><br></p>\n<p><strong>Course Overview </strong> </p>\n<p>Managing risk is an inherent part of delivering organisational success today. This course covers the fundamentals of enterprise risk management in a dynamic and interactive learning environment. The course will use the theory and practice of risk management in-line with international standards  (ISO 31000) and industry best practice.           \nThe course uses a combination of case studies, practical exercises and current affairs to bring material to life. Our trainers will bring you a wealth of industry expertise. On completion of this course you should have a solid understanding of how effective risk management can benefit your organisation.\n</p>', NULL, '\n<ul className=\"w-full\">\n<li><strong>Who should take this course</strong></li>\n<li>Anyone who needs to understand risk management and the role it plays in their area of business (operational and support managers)</li>\n<li>Anyone who needs to know how risk affects strategy and decision making- (Senior Executives)</li>\n<li>Anyone new to risk who wants to feel more confident in enterprise risk management</li>\n<li>Anyone who wishes to enter the risk profession</li><li>Anyone looking for a refresher to enhance their knowledge </li>\n<li>Anyone who needs to know about the relationship between risk management and audit and compliance (Risk, Audit and Compliance professionals)</li>\n</ul><p><br></p>', '<ol>\n<li><strong> Learning Objectives</strong></li>\n<li>Setting the scene</li></ol>\n<pUnderstanding risk maturity</p>\n<p>Understanding the risks around us</p>\n<p>2.What is Risk?</p>\n<p>Learn How to define Risk</p>\n<p>Understanding how risk management can be used to both maximise opportunities and minimise threats</p>\n<p>Understanding the principles and processes of risk management</p>\n<p>Establishing the attributes of effective risk management</p>\n<p>3.Context and Objectives</p>\n<p>Understanding the purpose and scope of risk management</p>\n<p>Establishing the relevant risk framework including roles & responsibilities, policies and appropriate guidance</p>\n<p>Develop an awareness of risk appetite</p>\n<p>4. Risk Assessment</p>\n<p>Gain and Awareness of identification techniques</p>\n<p>Assessing risk and selecting appropriate actions and controls using key tools, such as risk registers, risk bow-ties and risk matrices</p>\n<p>Practically applying risk assessment using case studies to identify and analyse</p>\n<p>5. Risk Action</p>\n<p>Understanding risk treatment plans and smart actions</p>\n<p>Recognising control effectiveness</p>\n<p>Awareness of Business resilience </p>\n<p>6. Monitoring, Communication and Reporting</p>\n<p>Techniques to keep the risk management process alive through effective monitoring and reviewing</p>\n<p>Develop and appreciation of effective risk reporting</p>\n<p>How to effectively communicate risk to all levels of your organisations and to external stakeholders</p><p><br></p>\n', 120000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 03:17:16', '2022-05-12 03:17:16', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1652449132/Tmc%20institute/Essentials-of-Risk-Management_xugcs8.png', 'Risk Management Courses'),
(11, 'Advanced Operational Risk Management', 'a', '<ul className=\"w-full\">\n<li><strong>Course Outline</strong></li>\n<li>Session 1 - Day One: Risk Identification Tools and Emerging Risks</li>\n<li>Session 2 - Day One: ORM Governance and Regulation</li>\n<li>Session 3 - Day One: Risk Reporting and Insight</li>\n<li>Session 4 - Day One: Implementing The Desired Risk Culture: A Method</li>\n<li>Session 1 - Day Two: Defining Risk Appetite Statements and Tolerance Limits</li>\n<li>Session 2 - Day Two: Internal Controls: Human Error and Control Design</li><li>Session 3 - Day Two: Root Causes Analysis, The Bow-Tie</li>\n<li>Session 4 - Day Two: Features and Types of Leading KRIs</li><li>Session 1 - Day Three: Cyber Threats and Information Security</li>\n<li>Session 2 - Day Three: Scenario Analysis: Governance, Stress Testing, and Assessment Methods</li>\n<li>Session 3 - Day Three: Reorganisation Risk and Project Management &amp; Outsourcing</li>\n<li>Session 4 - Day Three - Resilience and Reputation</li>\n</ul>', NULL, '<p><br></p>', '<p><br></p>', 180000.00, 'naira', NULL, NULL, '3 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 03:23:05', '2022-05-12 03:23:05', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661162064/Tmc%20institute/Operational-Risk-Management_2_ibvufz.png', 'Risk Management Courses'),
(12, 'Risk Appetite & Key Risk Indicators Masterclass', 'r', '<p>\n<strong>Risk Appetite</strong></p>\n<p>Understanding the differences between Appetite, Tolerance, Capacity and Universe. </p>\n<p>Methods to create Appetite statements that are effective at both a strategic and operational level (defining “expected” so everyone in the organisation can “spot deviations from the expected”).</p>\n<p><br></p><p><strong>Course Outline</strong></p><p>Day 1: Actionable Risk Appetite</p>\n<p>Session 1:  Risk In, Taxonomy and Interdependencies of risks</p><p>Session 2:  Principles and Design of Actionable Risk Appetite</p><p>Day 2: Identifying and selecting preventive KRIs</p><p>Session 3:  Preventive KRIs: a method</p><p>Session 4:  Root Cause Analysis for KRI Identification</p><p>Day 3: Control design &amp; KRI for specific risks</p><p>Session 5:  Control function and KRIs</p><p>Session 6:  Indicators for Compliance, Conduct, and Culture</p><p>Session 7:  KRIs for Project Risk management &amp; New Initiatives </p><p><br></p><p><strong>Key Risk Indicators</strong></p><p>Key Risk Indicators (KRIs) help organisations prevent risks and exploit the opportunities they offer. </p><p>This one day course will guide you through the use of KRIs, showing you how they can be used as a powerful management tool to improve risk awareness and the execution of business objectives.</p><p>Delivered through a mix of presentations, group discussions and guided exercises, </p>\n<p>you will learn, step-by-step, the methodology behind preventive KRIs. </p>\n<p>By the end of the course you will understand the role played by KRIs within an enterprise-wide risk management framework.</p>\n<p><strong>Key Risk Indicators</strong></p>\n<p>Key Risk Indicators (KRIs) help organisations prevent risks and exploit the opportunities they offer. This one day course will guide you through the use of KRIs, showing you how they can be used as a powerful management tool to improve risk awareness and the execution of business objectives.\n\nDelivered through a mix of presentations, group discussions and guided exercises, you will learn, step-by-step, the methodology behind preventive KRIs. By the end of the course you will understand the role played by KRIs within an enterprise-wide risk management framework.</p>', NULL, '<ul className=\"w-full\">\n<li><strong>Who should take this course</strong><li>\n<li>Risk Managers.</li>\n<li>Risk Practitioners.</li>\n<li>Risk Champions.</li>\n<li>Chief Risk Officers.</li>\n<li>Internal Auditors.</li>\n<li>Governance and Compliance Managers.</li>\n</ul>', '<ul className=\"w-full\">\n<li><strong>Learn about</strong></li>\n<li>The nature and types of preventive KRIs.</li><li>Identifying root causes and event drivers for each of your key risks.</li>\n<li>How indicators shape an organisation’s risk culture.</li>\n<li>Evaluating the effectiveness of draft KRIs you have been considering for use in your organisation.</li>\n<li>Designing an efficient indicator programme in a risk management framework.</li>\n</ul>', 180000.00, 'naira', NULL, NULL, '3 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 03:33:41', '2022-05-12 03:33:41', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1652449132/Tmc%20institute/Risk-Appetite-_-Key-Risk-Indicators-Masterclass_qxupge.png', 'Risk Management Courses'),
(13, 'Third-Party Risk Assessment and Management', 't', '   <p><strong>Course Overview</strong></p>\n<p>Extending business activities beyond organisational boundaries to create value for the organisation is not new.  \nHowever, the quantity and complexity of the relationships that now exist beyond organisational boundaries is increasing,\n as is regulatory and stakeholder scrutiny. While partnerships with third parties, including critical suppliers, \ncan be beneficial to the organisation on so many levels, such alliances can expose the organisation to many unknowns, \nand those unknowns will undoubtedly increase the level of risk. The key, then, is properly managing the infrastructure, systems, \nstaff and outside support to adequately manage that risk.</p>\n <p><strong>Course Breakdown</strong></p>\n<p>Participants will learn supply chain/third party risk management governance, analysis and process that can be applied across the organization at either an enterprise or a department level, </p>\n<p>within the business as usual and crisis situations. </p>', NULL, '<p>This course will be of benefit to risk managers, procurement managers, contract managers and business line managers who interact with third-party vendors and need to recognize the potential risks within the lemgal agreements and the services they provide, especially in situations where an entire business function is outsourced, multiple functions are consolidated to a single provider, the provider subcontracts and/or the provider works directly with the organisation’s customers.</p><p><br></p><p><strong>         It is beneficial for:</strong></p><ol><li>Procurement Professionals</li><li>Supply Chain Professionals</li><li>Ethics &amp; Compliance Professionals</li><li>Risk Management Professionals</li><li>IT Security Professionals</li><li>Legal Professionals</li><li>Environmental, Health &amp; Safety Professionals</li><li>Corporate Social Responsibility &amp; Accountability Professionals</li><li>Individuals with third party management, ownership, or oversight responsibilities</li></ol>', '<ul>\n<li><strong>Learning Objectives:</strong>\n<li>Setting the context for supplier / third part risk management including how supply chains are changing and understanding how those impact risks, definitions, alignment to supply chain / third party management and enterprise risk management, the importance and value derived from managing supplier/ third party risks</li>\n<li>Relationship to supply chain management and risk management including types of risks included such as Cyber, Compliance, Financial Crime, business disruption and so on</li>\n<li>The Challenges and pitfalls of managing supply chain / third party risks</li>\n<li>Supply Chain / Third-Party Risk Framework Overview</li>\n<li>Assessing third party risks and controls – process requirements, tools and techniques</li>\n<li>Third-Party Risk Governance including strategic risk management and resilience, risk response options, operating models and monitoring & reporting</li>\n<li>Supply chain / Third-Party Risk Management and the Procurement Lifecycle – from onboarding to exit</li>\n<li>>Managing supply chain / third party risk management information</li>\n<li>Implementing Supply Chain and Third-Party Risk Management – from risk to resilience</li>\n<li>The digitisation of Supply Chain and Third-Party Risk Management – risks and opportunities</li>\n</ul>\n<ul className=\"w-full\">\n<li><strong>Learning Outcomes:</strong></li>\n<li>Effectively manage due diligence and supply chain / third-party risk.</li>\n<li>Understand the challenges and pitfalls of managing third-party risk</li>\n<li>Achieve success capitalizing on third-party relationships while maintaining compliance</li>\n<li>Facilitate ongoing monitoring of critical suppliers / third-party partners.</li>\n<li>Define a supplier/ third -party management lifecycle for managing and monitoring supplier/third party relationships</li>\n<li>Establish supplier/third party management ownership and accountability across the 3 lines of defence</li>\n<li>Provide supplier / third party management process consistency and enhance cross-functional collaboration</li>\n<li>Communicate more effectively with suppliers /  third parties on matters of risk and compliance</li><li>Deliver effective supplier / third-party governance and assurance to the board of directors, regulators, and stakeholders</li>\n<li>Monitor metrics to establish effectiveness or supplier / third-party  risk management</li>\n<li>Integrate third-party/supplier risk management with other key processes such as procurement.</li>\n</ul>', 120000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 03:54:23', '2022-05-12 03:54:23', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1652449135/Tmc%20institute/Third-Party-Risk-Assessment-and-Management_iryc8x.png', 'Risk Management Courses'),
(14, 'Cyber Security Risk Management', 'c', '<p><strong>Course Overview</strong></p>\n<p>A comprehensive overview of risk and management principles and practices as applied in a cyber security context.</p><p><strong>Description</strong>\n</p><p>In this course you will gain a solid understanding of risk management principles, processes,</p>\n<p> frameworks and techniques that can be applied specifically to cyber security as well as risk in general.</p>\n<p>You will learn how to identify, assess and articulate risk as well as options available for treating risk and which may be most appropriate for your situation.</p>\n<p>This course also provides examples of tools and techniques as well as useful tips that can help you to successfully implement and maintain a risk management framework within your organization.</p>\n', NULL, '<ul className=\"w-full\">\n<li><strong>Who this course is for</strong></li>\n<li>Information Technology Managers</li>\n<li>Information/Cyber Security Managers</li>\n<li>Information Security Professionals</li>\n<li>Anyone with an interest in gaining a general understanding of risk management as well as how it applies to information security</li>\n</ul>\n', '<ul className=\"w-full\">\n<li><strong>What you\'ll learn</strong></li>\n<li>Develop an understanding of what risk is and how it can be managed.</li>\n<li>How to create a risk management framework within your organisation.</li>\n<li>How to identify, assess and articulate risk as well as identifying options for treatment and determining which is the most appropriate.</li>\n<li>How to perform detailed analysis of risk and develop risk treatment plans.</li>\n<li>How to apply risk management concepts in practice including developing a risk register, governance models, risk bowties and reporting.</li>\n</ul>', 120000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 04:01:12', '2022-05-12 04:01:12', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1652449135/Tmc%20institute/Cyber-Security-Risk-Management_d3midx.png', 'Risk Management Courses'),
(15, 'Mitigate Fraud Risk with Effective Internal Controls', 'm', '<p><strong>Course overview</strong></p>\n<p>This course is designed to examine the typical red flags and highlight the best practices on fraud prevention.</p>\n<p><strong>Description</strong></p>\n<p>This course is designed to examine the typical red flags and highlight the best practices on fraud prevention and monitoring initiatives.</p>\n<p>In this course, we will explore best practices on how to:</p><p>Develop a fraud risk management program;</p><p>Prepare a fraud risk assessment;</p>\n<p>Deter, prevent or detect fraud risks;</p>\n<p>Monitor fraud risks.</p>', NULL, '<p><br></p>', '<ul className=\"w-full\">\n<li><strong>What you\'ll learn</strong></li>\n<li>Explore key factors contributing to fraudulent behaviors.</li>\n<li>Identify common fraud schemes.</li>\n<li>Discover the process to mitigate fraud risks.</li>\n<li>Explore the fraud risk assessment process.</li>\n<li>Identify key anti-fraud controls.</li>\n</ul>', 120000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 04:08:59', '2022-05-12 04:08:59', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1652449136/Tmc%20institute/Mitigate-Fraud-Risk-with-Effective-Internal-Controls_gtrevo.png', 'Risk Management Courses'),
(16, 'Introduction to Risk Management', 'i', '<ul className=\"w-full\">\n<li><strong>Course Outline</strong></li>\n<li>Context of risk management</li>\n<li>Risk vocabulary</li>\n<li>Risk standards / regulation &amp; governance</li>\n<li>The five stages of a risk management framework:</li>\n<li>Objectives</li>\n<li>Appetite</li>\n<li>Risk identification & assessment</li>\n<li>Risk Responses</li>\n<li>Risk assurance</li></ul>', NULL, '<p><br></p>', '<p><br></p>', 120000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 04:12:16', '2022-05-12 04:12:16', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1652449138/Tmc%20institute/Introduction-to-Risk-Management_hutlc0.png', 'Risk Management Courses'),
(17, 'Risk  Managers Master Class', 'r', '<p><strong>Course Overview</strong></p>\n<p>This masterclass is based on three days of highly intensive strategic risk training that is designed for a senior audience. </p>\n<p>To ensure full effect, it is advisable to have a broad understanding of risk management before participating in this course.</p>', NULL, '<ul className=\"w-full\">\n<li><strong>Who should take this course</strong></li>\n<li> Senior risk professionals</li>\n<li> Business leaders (CEOs, heads of function, senior managers)</li>\n<li>Assurance providers, e.g., internal and external auditors</li>\n<li>Non-executive directors (particularly those with responsibility for audit and risk)</li>\n<li>Professional advisors wishing to provide more expert support for their clients’ market</li></ul><p><strong>Learning objectives</strong></p><ul>\n<li> Gain an advanced understanding of GRC</li><li>Examine external risks and how they can</li></ul><p><strong> impact your organization</strong></p><ul><li> \nIdentify key risk in your organization</li><li> Learn tools and techniques to measure and monitor control effectiveness</li>\n<li>Identify key management processes required in your organization</li><li>Forward thinking: Horizon scanning and scenario planning</li>\n<li> How to communicate key risks to and with senior management teams and stakeholders</li>\n</ul>', '<p><strong>The learning objective of this course</strong> is to address business dilemmas and help create a culture in your organization that ensures that</p><p>risk management is not just a checklist feature and that it’s an embedded component in your organization\'s DNA. You will learn what the</p><p>most important processes are to manage in your organization and what processes can and should be used to help your organization remain in “safe”</p><p>mode.</p><p><br></p><p>Senior Risk Masterclass</p>', 180000.00, 'naira', NULL, NULL, '3 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 04:20:32', '2022-05-12 04:20:32', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1652449135/Tmc%20institute/Risk--Managers-Master-Class_uqjoso.png', 'Risk Management Courses'),
(18, 'Detecting and Preventing Vendor Fraud', 'd', '<p><strong>Course Overview</strong></p>\n<p>This training on detecting and preventing vendor fraud will help the attendees to identify  the different types of vendor-related</p>\n<p> fraud schemes and implement best practice anti-fraud control strategies.</p>\n<p><br></p><p><strong>Why Should You Attend:</strong></p>\n<p>Billing schemes are the most common type of external fraud schemes threatening most organizations.</p>\n<p>Participants will learn how to identify varieties of vendor-related fraud schemes damaging organizations of all kinds, including small businesses and non-profits, and determine how shrewd fraudsters abuse the payment process to embezzle funds for years before being detected—and how to shorten the time before discovery.</p>\n<p>This training will guide you through each of the critical steps, leaving you with practical “how-to” guidance for minimizing your organization’s exposure to the large and growing variety of vendor schemes and collusive frauds.</p><p>This training will enable participants to quickly recognize the red flags of external as well as internal vendor fraud in your operations and financial records and will assist in providing cost-effective anti-fraud controls to protect against the growing army of real and phony vendor fraudsters.</p><p><br></p><p><br></p>', NULL, '<ul>\n<li><strong>Who Will Benefit</strong></li>\n<li>Internal and external audit professionals</li>\n<li>Purchasing/procurement managers and staff</li>\n<li>Accounting and audit practitioners</li>\n<li>AP managers</li><li>Senior financial management seeking to reduce their vulnerability to costly frauds</li>\n<li>Compliance and ethics managers</li>\n<li>CFO’s/senior financial managers</li>\n<li>Security personnel</li></ul>', '<p><br></p><p><strong>Learning Objectives:</strong></p><p>Participants will be able to:</p><p>Conduct a quick and cost-effective fraud risk assessment for your payments/vendor operations</p><p>Implement effective vendor/billing fraud detection techniques and build audit practices to detect red flags of common schemes.</p><p>Implement best-practice anti-fraud control strategies for vendor fraud</p><p>Advise management on fraud risk mitigation through transaction monitoring, technology/information technology tools and low-cost internal controls</p><p>Recommend better anti-fraud management structure (Board of Directors/Executive Management, Line Management, Compliance, General Counsel, etc.)</p><p><br></p><p><strong>Areas Covered:</strong></p><p>Introduction</p><p>Statistical overview of the fraud problem</p><p>Who commits vendor fraud</p><p>Lessons from fraudsters</p><p>Common Types of Vendor Fraud Today</p><p>Billing fraud schemes</p><p>Vendor master file schemes</p><p>Double billing schemes</p><p>Internal manipulation of vendor master file</p><p>Internal perpetration of shell company/pass-through vendor schemes</p><p>Kickbacks/bribery</p><p>Conflicts of interest</p><p>Red Flags of Vendor Fraud</p><p>Conducting a fraud risk assessment and recognizing the red flags of vendor and collusive fraud</p><p>The fraud risk mitigation cycle</p><p>Implementing an organization-wide system for detecting, preventing and investigating fraud</p><p>Fraud Detection</p><p>How vendor frauds are most often detected</p><p>Additional general detection methods</p><p>Data mining/analytics for fraud detection</p><p>Examples of fraud audit techniques at work</p><p>Anti-fraud Controls for Vendor Fraud</p><p>Internal controls and other fraud prevention measures</p><p>Who should manage anti-fraud activities for AP</p><p>Internal controls: Dos and Don’ts</p><p>General controls: Segregation of duties, delegation of authority, background investigation</p><p>Specific AP Operations-level controls for foiling vendor schemes.</p>', 180000.00, 'naira', NULL, NULL, '3 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 04:28:03', '2022-05-12 04:28:03', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1652449138/Tmc%20institute/Detecting-and-Preventing-Vendor-Fraud_dt6bb7.png', 'Risk Management Courses'),
(19, 'Best Practices in Managing Vendor Fraud Risk', 'b', '<p><strong>Course overview</strong></p>\n<p>Procurement fraud is one of the most, if not the most, common fraud schemes within an organization. \nProcurement is where the money is and procurement fraud can be costly. \nAbout 29 procurement fraud schemes can occur in any stage of the whole procure-to-pay cycle. \nSome of these frauds involve conflict of interest, corruption and collusion,\n which are very difficult to be detected. Managing procurement fraud risk have always been a painful and unfruitful attempt by many organization.\n It need not be – if you are equipped with the right knowledge on procurement process, systems, fraud modus operandi and red flag, \nas well as the skills to detect them early! Using appropriate strategies and tools we can even prevent fraud and manage procurement risk to the lowest!</p>\n<p>From this course, participants will gain complete overview of procurement processes and systems.\n All stages within procurement lifecycle will be discussed to reveal the “inside” of sourcing and buying activities and its unspoken rules. \nThe course aims to cover the learning and application of tools and techniques procurement officers and auditors possess to address potential frauds and lapses in purchasing lifecycle.</p><p>Upon completion of this course, the participant will:</p>\n<p>able to assess or audit the procurement to identify red flags of fraud schemes</p>\n<p>deploy process and systems to minimize or eradicate procurement control weakness</p>\n<p>understand the process and procedures of a systematic procurement function</p>\n<p>able to detect and investigate suspicious red flags of procurement fraud</p>\n<p>implement procurement fraud risk prevention strategy and processes</p><p><br>\n</p>', NULL, '<ul className=\"w-full\">\n<li><strong>TARGET AUDIENCE</strong></li>\n<li>CFO, Finance Manager, Accountant</li>\n<li>Procurement, HR managers</li>\n<li>Internal auditor, Audit manager, audit directors</li><li>Control and compliance officers, Ethic officer, Risk manager,</li><li>Forensic auditor, Loss prevention and fraud prevention officer</li><li>External auditors, Regulatory agency staff</li></ul>', '<p><strong>OUTLINE</strong></p><p>1. Overview of Procurement Fraud</p><p>Reasons why Difficult to Detect</p><p>2. 29 Schemes in 8 Categories of Procurement Frauds</p><p>Corruption</p><p>Specification manipulation</p><p>Fake buys</p><p>Delivery schemes</p><p>False claims</p><p>Bidding schemes</p><p>False info</p><p>Forgery</p><p>3. Detecting Procurement Frauds Red Flags</p><p>4. Investigating Procurement Frauds</p><p>Document Review and Data analytic</p><p>Surveillance and Digital Forensic</p><p>Information Seeking Interviewing</p><p>Confession Seeking Interview</p><p>Basic concept of Evidence</p><p>Effective Forensic Investigation Framework</p><p>5. Setting Governance on Procurement Fraud Risk</p><p>Strategies (Tone at the top)</p><p>Structure (Centralized, Decentralized Procurement, IPO)</p><p>Process (COC, Rotations, Forced leave, WB Hotlines)</p><p>People (Position screening, Training, Rewards and Incentive)</p><p>Governance Structure (Oversight structure, 1st, 2nd and 3rd line defense)</p><p>Systems (authorization matrix, SOD, system validation)</p><p>6. Common Procurement Principles, Processes and Challenges</p><p>Sourcing vs Buying</p><p>Low cost vs best value</p><p>RFx, tender procedures, online procurement platform</p><p>Market place platforms (vendor specific, non-vendor specific)</p><p>Procurement Operational Challenges</p>', 180000.00, 'naira', NULL, NULL, '3 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 04:37:04', '2022-05-12 04:37:04', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661159608/Tmc%20institute/Best-Practices-in-Managing-Vendor-Fraud-Risk_ywnw7h.png', 'Risk Management Courses'),
(20, 'Advanced Enterprise Risk Management', 'a', '<p><strong>Course Outline</strong></p>\n<p>Gain a thorough Understanding of the Characteristics of an Advanced ERM Process</p>\n<p>Understand the Techniques to take fully embed your ERM process by the use of Global Risk scenarios</p>\n<p>Develop an ERM Strategic Roadmap</p>\n<p>Learn how to use sophisticated Risk Evaluation Techniques such as Delphi Monte Carlo and Bayesian Network</p>\n<p>Understand how to Identify and Manage Emerging Risks</p><p>Develop meaningful Key Risk Indicators (KRI’s)</p>\n<p>Re-focus the Risk Register to be a simple to follow Record of the Risks (Using a Global Best Practice Model)</p>\n<p>Incorporate inherent, residual and target Risk Assessments for each Risk</p>\n<p>Explicitly Link Risk with Decision Making at all Levels of the Business</p>\n<p>Energise all Levels of Management and Staff to Manage Risks Effectively</p>\n', NULL, '<p><strong>Who Should Attend?</strong></p>\n<p>Chief Risk Officers</p>\n<p>Risk Managers</p>\n<p>Managers and Directors responsible for the risk management function or process</p>\n<p>Heads of Internal Audit</p>\n<p>Heads of Assurance functions</p><p>Senior Finance Professionals</p>', '\n <p><strong>Objectives</strong></p>\n<p>Link the ERM process directly with the strategic plans</p>\n<p>Challenge and overcome the negative views of risk</p>\n<p>Instil a risk culture of ‘what do we need to get right’ rather than ‘ what can go wrong’</p>\n<p>Develop clear risk appetite statements for the Board and each business function</p>\n<p>Appreciate why ERM is not just another project or fad</p>\n', 180000.00, 'naira', NULL, NULL, '3 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 04:40:37', '2022-05-12 04:40:37', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1652449138/Tmc%20institute/Advanced-Enterprise-Risk-Management_pg2wdo.png', 'Risk Management Courses'),
(21, 'Key Elements in Managing Operational Risk', 'k', '<p>Course Overview</p>\n<p>This course sets out a step-by-step approach covering the fundamental steps required to identify, manage, and mitigate.</p>\n<p><strong>Description</strong></p>\n<p>This course sets out a step-by-step approach covering the fundamental steps required to identify, manage, and mitigate operational risks.\n We cover key aspects of operational risk management including critical issues such as risk analysis, risk appetite, probability, impact,\n risk mitigation process, and prioritization.</p><p>Inadequate or failed internal processes, people and systems or external events are the key \n“risk drivers” in operational risk. These “risk drivers” are exceptionally wide-ranging and cover virtually every element of the operations in all \nforms of business and other organizations.</p>\n<p>As our source we draw on the financial industries approach to operational risk management as defined in the Basel Accords. \nThe Basel view, from the definition to the process and principles used, is in the presenter\'s view, the most succinct way of presenting a practical \ninterpretation of operations risk management that is easily replicated for other business and operational entities.</p>\n<p>The effective management of all operational risks is a major component of an organization’s risk management strategy.</p>', NULL, '<p><br></p>', '<p>What you\'ll learn</p>\n<p>Discover the processes used in the management of operational risk.</p>\n<p>Identify the different types of operational risks.</p><p>Explore the methods used in identifying and evaluating the different operational risks.</p>\n<p>Explore critical issues such as risk analysis, risk appetite, probability, impact,\n the risk mitigation process, prioritization and risk management responsibilities.</p>', 120000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 04:47:02', '2022-05-12 04:47:02', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1652449138/Tmc%20institute/Key-Elements-in-Managing-Operational-Risk_zxy98w.png', 'Risk Management Courses'),
(22, 'Introduction to Operational Risk Management', 'i', '<p><strong>Course Overview</strong></p>\n<p>Credit Risk, Compliance Risks and Liquidity Risk</p>\n<p>You will learn about Operational Loss Database</p>\n<p><strong>Description</strong></p>\n<p>Operational Risks are perhaps the most significant risks that organizations face on daily basis. Most of the corporate failures occurred in the past \ndecade, are due to the operational risks occurrences causing operations failures. \nCompanies and institutions are therefore, focused on managing the operational risks.</p>', NULL, '<p><strong>Who this course is for</strong></p>\n<p>This course should be attended by:</p>\n<p>Risk management professionals</p>\n<p>Compliance professionals</p>\n<p>Bankers</p>\n<p>Risk Advisors and consulting firms</p>\n<p>Branch managers / Operation managers</p>\n<p>Internal auditors</p>\n<p>Internal controls professionals and</p><p>Others who want to learn the risk management and its components</p>', '<p><strong>How Will You Be Benefitted By Attending This ORM Course?</strong></p><p><strong>By attending this course you will:</strong></p><p>- be able to link knowledge gained with practical aspects, as to how an organization conducts Operational Risk Assessment and Management</p><p>- be able to talk confidently with risk management professionals and consultants regarding ORM</p><p>- be able to work in consultancy firms or organizations in their risk management departments specially ORM departments</p><p>- be able to perform more research and analysis on ORM practices</p><p>- be able to pursue a career progression in risk management field.</p>', 120000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 04:54:56', '2022-05-12 04:54:56', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661161951/Tmc%20institute/Operational_Risk_Management_tzb1fy.png', 'Risk Management Courses'),
(24, 'Responding to Risk', 'r', '<p><br></p>', NULL, '<p><br></p>', '<div>\n<p><strong>What you\'ll learn</strong></p>\n<p>\nIdentify the inputs to the Plan Risk Responses process.\nIdentify strategies for responding to negative risks.\nIdentify strategies for responding to positive risks.\nRecognize examples of using contingent response strategy.\nRecognize the outputs of the Plan Risk Responses process.\nRecognize the purpose of the Implement Risk Responses process.\nIdentify the inputs to the Monitor Risks process.\nRecognize examples of risk audit outcomes.\nDistinguish between technical performance analysis and reserve analysis measurement criteria.\nRecognize appropriate risk response strategies.\nRecognize the outputs of the Monitor Risks process.\nDemonstrate your understanding of the Plan Risk Responses, Implement Risk Responses, and Monitor Risk processes.</p>\n</div>', 120000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 04:58:30', '2022-05-12 04:58:30', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1652449144/Tmc%20institute/Responding-to-Risk_krx85h.png', 'Risk Management Courses'),
(25, 'Compliance Risk Management', 'c', '<p><strong>Course Overview</strong></p>\n<p>For several years, we have seen countless news items concerning securities fraud, ethical lapses, money laundering, conflicts of interest,\n and insider trading. More often than not, these cases result in loss of business, reputation, revenue, and importantly, trust.</p>\n<p>As regulatory authorities globally turn their attention to higher expectations in the control environment, the compliance function is quickly becoming an \nimportant driver in risk management strategies. Financial services firms globally are strengthening their compliance functions with stronger training, \nsupervisory, advisory, reporting and surveillance, and control room resources to meet the intense regulatory scrutiny now expected.</p>\n<p>Attend this practical 3-day course to obtain a deep understanding of and appreciation for the necessity of a strong and well-equipped compliance \nfunction within any financial services institution. Through lectures, group exercises, interactive participation, simulation, and case studies, \nyou will review the need for financial services regulation and  the markets, participants, institutions, and instruments over which regulation proceeds. \nYou will also examine the role of the compliance function as a risk mitigator, the various methods and approaches to compliance with regulatory requirements,\n establishment of a well-functioning compliance department, and the skills required to succeed as a compliance officer.</p>\n<p>Through intense analysis of specific cases concerning insider trading, compliance and ethical lapses, money laundering, rule violations both inadvertent \nand purposeful, you will gain key insights to help reduce the risk of regulatory and compliance problems and to foster a culture of compliance within their \nown firms.</p>', NULL, '<p><strong>Who should attend this course?</strong></p>\n<p>This course has been specifically designed for the benefit of:</p>\n<ol><li>New Compliance Officers</li>\n<li>Risk Managers</li>\n<li>Control Function Managers</li>\n<li>Legal Department Managers</li>\n<li>Management Consultants</li>\n<li>Equities Sales and Trading Desk Operations Managers</li>\n<li>Fixed Income Sales and Trading Desk Operations Managers</li>\n<li>Investment Banking Administrative Officers</li><li>Treasurers</li>\n<li>Financial decision makers in corporations</li>\n<li>Strategists</li></ol>', '<p><strong>Attend this topical 3-day course and learn:</strong></p><p>How to successfully structure and manage an effective compliance function</p><p>The immediate impact of an ineffective compliance program</p><p>The comprehensive loss of revenue, trust, and reputation resulting from a weak compliance environment</p><p>How regulatory changes can quickly impact your organization and bottom line</p><p>How to prepare for intense regulatory scrutiny and examinations</p><p>From multiple case studies that globally illustrate cultures of compliance and strong compliance departments</p>', 180000.00, 'naira', NULL, NULL, '3 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 05:02:50', '2022-05-12 05:02:50', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661160574/Tmc%20institute/Compliance_Risk_Management_k9i7od.png', 'Risk Management Courses'),
(26, 'Liquidity Risk Management', 'l', '<p><strong>Course overview</strong></p>\n<p>Liquidity risk was one of the main drivers of the 2008 global financial crisis. </p>\n<p>This course will give an overview of the challenges of managing liquidity risk as well as recommendations to address this important risk.</p>', NULL, '<p><br></p>', '<p>Key Learning Outcomes</p>\n<p>Review liquidity management lessons learned from the crisis</p>\n<p>Use a structured approach to assess liquidity risk management, asset and liability management and funding strategy</p>\n<p>Understand how banks forecast, control and stress-test their liquidity sources and uses (on and off-balance sheet) and build a contingency funding plan to address stress cash outflows</p><p>Identify banks with weak liquidity and contingency planning within the context of the bank’s role within the financial system</p>\n<p>Develop knowledge of regulations and supervisory guidance on the management of bank liquidity</p>', 120000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 06:07:58', '2022-05-12 06:07:58', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1652449140/Tmc%20institute/Liquidity-Risk-Management_pati1d.png', 'Risk Management Courses'),
(27, 'Managing Risk in Electronic Banking', 'm', '<p><br></p>', NULL, '<p><strong>Who should attend?</strong></p>\n<p>This course is a must for every Risk Manager as well as all who are involved in all aspects of Electronic Banking Services including :</p>\n<p>Banking & Other financial institutions Product Designers</p>\n<p>Banking & Other financial institutions Supervisors</p>\n<p>Operations Risk Managers</p>\n<p>Bank and other financial institutions Operation Managers</p>\n<p>Bank Retail & Corporate Customer Managers</p>\n<p>Staff Corporate Treasurers & Associated Accounting Staff</p>\n<p>Internal & External Auditors</p>', '<p><strong>Why Attend</strong></p>\n<p>Rapid technological changes in recent years have revolutionized the way banking services and products are delivered to corporate and consumers alike,\n so much so that keeping up with these changes is like trying to tame a tiger.</p><p>We are all familiar with how the internet, the cell- phone, smartcards \nand even ATMs have changed our lives. These changes have dramatically modified and in some cases increase some of the traditional risks associated with \nbanking activities, in particular strategic, operational, legal and reputational risks. In turn this has influenced the overall risk profile of banking.</p>\n<p>Electronic banking carries its own unique characteristics which increases and modifies some of the traditional risks associated with banking activities. \nThese characteristics include:</p><p>Extraordinary speed of change related to technological and customer service innovation.</p><p>Universal and global nature\n of open electronic networks</p>\n<p>Integration of electronic banking applications with legacy computer systems, and</p><p>Increasing dependence of \nbanks on third parties that provides the necessary information technology on which electronic banking depends.</p>\n<p>According to all relevant regulatory bodies, it is incumbent upon the boards of Directors and banks and other financial institutions’ senior management \nto take steps to ensure that their institutions have reviewed and modified where necessary their existing risk management policies and processes to cover their current or planned electronic banking activities.</p><p>This course examines how the traditional risk profile has shifted and focuses on these changes and what needs to be done to effectively implement an efficient operational risk regime that includes all the required controls. In essence, we examine how to tame this tiger known as electronic banking.</p>', 120000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 06:11:15', '2022-05-12 06:11:15', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661161614/Tmc%20institute/Managing-Risk-in-Electronic-Banking_btus5h.png', 'Risk Management Courses');
INSERT INTO `course_infos` (`id`, `coursename`, `firstletter`, `coursedetails`, `certification`, `whothiscoursefor`, `learning`, `price`, `currency_name`, `instructor`, `rating`, `duration`, `lesson`, `quizzes`, `certificate`, `language`, `access`, `created_at`, `updated_at`, `picture`, `MainHead`) VALUES
(28, 'Risk-Based Approach to AML & CFT', 'r', '<p><strong>Course Overview </strong></p>\n<p>The Risk Based Approach to AML & CTF programme will elevate your knowledge of definitive risk management strategies through simulation exercises,\nclass lecture, and group work and teach you how to design and execute effective Anti-Money Laundering and Anti-Terrorist Financing Programs \nand Policies.</p>\n', NULL, '<p><strong>Suitability - Who should attend?</strong></p>\n<p>This course has been specifically designed for the benefit of:</p><p>Middle and Back Office Personnel</p>\n<p>Front Office Sales Personnel</p><p>Compliance, Risk, Governance Officers</p><p>Regulators</p>\n<p>Career Switchers into Compliance</p><p>Administrative Officers</p><p>Supervisors</p>\n<p>System developers</p><p>Risk managers</p><p>Reporting specialists</p>\n<p><strong>Prerequisites:</strong></p>\n<p>The course assumes a general familiarity with financial markets, instruments and financial services.</p>', '<p><strong>Outcome / Qualification etc.</strong></p><p>Attend this highly practical three-day training course and learn how to:</p><p><br></p><p>Design an effective Anti-Money Laundering Program to support your organizations Compliance and regulatory obligations</p><p>Establish and strengthen controls to combat terrorist financing through financial institutions</p><p>Understand best practices and guidelines designed to detect, investigate, report, escalate and resolve red flags, money laundering, and terrorist financing issues</p><p>Understand money laundering and terrorist financing processes</p><p>Identify both common and uncommon red flags and questionable behaviour</p><p>Be aware of the dangers of money laundering to clients, your organization and yourself</p><p>Be well prepared to help prevent criminals from using your firm to launder money and facilitate terrorist financing</p><p><strong>Training Course Content</strong></p><p><strong>Day 1</strong></p><p>Setting the Stage: Anti-Money Laundering and Combatting Terrorist Financing Basics</p><p><br></p><p>Current international cases, fines, and legal judgments</p><p>AML/CTF success stories</p><p>Corporate and economic implications of AML/CTF</p><p>Key terms, definitions, and institutional differences</p><p>Legal and regulatory background to AML/CTF</p><p>The Money Laundering Process</p><p><br></p><p>Placement, layering, and integration</p><p>Red flags: unique to each business line</p><p>Relationship to terrorist financing and blocked assets</p><p>Roles: who’s responsible for what?</p><p>Terrorist Financing Operations</p><p><br></p><p>Funds generation</p><p>Transactions</p><p>Legal and illegal means</p><p>Relationship to money laundering</p><p><br></p><p>Case Study and Simulation Exercise 1:Delegates will, through review of a case, be assigned roles (customers, business line, middle and back office, compliance and legal, regulators) to analyze transaction data for possible money laundering activity/terrorist financing, escalation, reporting, investigating, and resolution)</p><p><br></p><p>Where It All Starts: The AML and CFT Rubric: Establishment</p><p><br></p><p>The Anti-Money Laundering and CFT Program</p><p><br></p><p>Design of an AML/CTF Policy</p><p><br></p><p>Effective Elements of the AML/CTF Program Policy</p><p><br></p><p>Identifying the Effective Elements</p><p><br></p><p>Designation of an AML and CTF Compliance Officer (s)</p><p>Auditing for AML and CTF: internal and external</p><p>Ongoing training</p><p>Due diligence and customer identification</p><p>Scenario, control, and systems testing</p><p>Supervision</p><p>Reporting and information flow: suspicious activity reports</p><p>Surveillance, systems, and investigations</p><p>Staffing, credentials, experience, and characteristics</p><p>Escalation</p><p>Record keeping</p><p><strong>Day 2</strong></p><p>Combating Terrorist Financing</p><p><br></p><p>CTF Goal: to aggressively identify, escalate and deter terrorist organizations’ funding and transactions networks</p><p><br></p><p>FATF Recommendations</p><p><br></p><p>FATF Global Money Laundering and Terrorist Financing Threat Assessment (GTA)</p><p><br></p><p>Risk Assessment Methodologies</p><p><br></p><p>FATF: Inclusion vs Exclusion</p><p><br></p><p>CTF Resources</p><p><br></p><p>Intelligence analysis</p><p>Sanctions administration</p><p>Enforcement</p><p>Financial regulatory action</p><p>Policy expertise</p><p>Implementation and Application of the Rubric</p><p><br></p><p>Policies, Procedures, and Controls: Implementation</p><p><br></p><p>Designation of an AML Compliance Officer</p><p>Auditing for AML: internal and external</p><p>Ongoing training</p><p>Due diligence</p><p>Customer identification</p><p>Supervision</p><p>Reporting and information flow: suspicious activity reports</p><p>Surveillance, systems, and investigations</p><p>Staffing, credentials, experience, and characteristics</p><p>Escalation</p><p>Record keeping</p><p>Risk Based Approach</p><p><br></p><p>Geography and country risk</p><p>Business risk</p><p>Product risk</p><p>Reputational risk</p><p>Transaction risk</p><p>Client risk</p><p>Process risk</p><p>Suspicious Activity Reporting</p><p><br></p><p>Trigger areas</p><p>Information flows</p><p>Preventing terrorist financing through AML </p><p><br></p><p><br></p><p>Danger Zones: Failures</p><p><br></p><p>AML/CTF compliance policy</p><p>Faulty testing</p><p>Unsatisfactory training and non-compliance with training requirements</p><p>Insufficient resources: systems, technology, and staff</p><p>Lack of follow up: connect the dots</p><p>Organizational Settings</p><p><br></p><p>Laws, rules and regulations</p><p>International organizations</p><p>Information sharing</p><p>Industry organizations</p><p>Forensic Review of Surveillance and Investigation</p><p><br></p><p>Dissecting an AML/CTF Investigation</p><p>A day in the life of an AML/CTF surveillance team</p><p>Identification of red flags</p><p>Business line specific AML/CTF issues</p><p>Technology and AML/CTF: technology-based schemes, and technology-based monitoring, surveillance, and detection</p><p><strong>Day 3</strong></p><p>Know Your Customer Best Practices</p><p><br></p><p>Policy statement</p><p>Rationale</p><p>Customer identification programme</p><p>Application of the customer identification programme</p><p>Covered customers</p><p>Data collection: individuals, institutions, official/governmental organizations</p><p>Verification: documentary vs non-documentary</p><p>Logical consistency</p><p>Additional measures</p><p>Lack of verification</p><p>Data from external financial institutions</p><p>Service providers</p><p>High risk accounts</p><p>Special Topics</p><p><br></p><p>Shell banks</p><p>Politically exposed persons</p><p>Correspondent banks and foreign banking relationships</p><p>Enhanced due diligence</p><p>Identity theft</p><p>Charitable organizations</p><p>Volume</p><p>Monitoring Customer Relationships</p><p><br></p><p>Consistency</p><p>Pattern analysis</p><p>Systems</p><p>Aggregation</p><p>College knowledge vs flagrant indifference</p><p>Risk rating</p><p>Monitoring credit relationships</p><p>AML/CTF IN Practice: Implications for Business Lines</p><p><br></p><p>Internet gambling</p><p>Insurance fraud</p><p>Tax evasion</p><p>Wire transfers</p><p>Check collection</p><p>Funds transfer and money transmittal</p><p>Card dystems</p><p>Currency and monetary systems</p><p><br></p>', 120000.00, 'naira', NULL, NULL, '3 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 06:18:06', '2022-05-12 06:18:06', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661162418/Tmc%20institute/Risk-Based-Approach-to-AML_fb2eqh.png', 'Risk Management Courses'),
(29, 'Market Risk Management', 'm', '<p><strong>Course Overview</strong></p>\n<p>Do you know</p>\n<p>Why do companies and institutions focus on managing their funds and working capital?</p>\n<p>How do banks, financial institutions, and companies use and save their funds?</p>\n<p>How the financial institutions, banks, and companies are exposed to market risk factors?</p>\n<p>Why Board of Directors and Senior Management of the companies are concerned with the management of market risk?</p><p><br></p>', NULL, '<p><br></p>', '<p><strong>What you\'ll learn</strong></p><p>In this course students shall learn about Risks, Types of Risks and Risk Management Process</p><p>Students will learn about the Market Risk Management Principles</p><p>You will learn different Models of Market Risk Management and Techniques used</p><p>You will learn about the Governance structure, Role of Market Risk Management Department, models and examples</p><p><br></p><p>In this course you will:</p><p>Get an insight into how the companies and institutions focus on risk management practices including market risk management to Save their funds and avoid financial losses</p><p>Learn risks and different types of risks with examples</p><p>Risk management process</p><p>Governance structure in risk management and market risk management MRM</p><p>Role of Board and Management Committees in managing market risk management MRM</p><p>Types of market risk management and examples</p><p>Principles of market risk management, examples, and scenario</p><p>Role of Market Risk Management (MRM) Function (MRM is a unit of the Risk Management Department in an organization)</p><p>Monitoring</p><p>Market risk management models and examples such as Value at Risk (VaR) and Interest Rate Risk</p><p>Be able to understand and calculate different market risk exposures using data</p><p><br></p><p><br></p>', 120000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 06:32:44', '2022-05-12 06:32:44', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661161726/Tmc%20institute/Market-Risk-Management_haj5bt.png', 'Risk Management Courses'),
(30, 'Operational Risk Management Booster', 'o', '<div>\n<p><strong>Course Outline</strong></p>\n<p>\nSession 1: Integrated Risk Management Framework\nSession 2: Risk identification and horizon scanning tools\nSession 3: Maturity Benchmark and Actionable Risk Appetite\nSession 4: Risk and control assessment\nSession 5: Steps for preventive key risks indicators\nSession 6: Conduct and Culture: embedding risk management\nSession 7: Scenarios: Assessing rare events\nSession 8: Aggregating and reporting risk information\n</p>\n</div>', NULL, '<p><br></p>', '<p><br></p>', 120000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 06:35:25', '2022-05-12 06:35:25', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661162113/Tmc%20institute/Operational-Risk-Management-Booster_ikjrbg.png', 'Risk Management Courses'),
(31, 'Embedding Risk Management into the Corporate Culture', 'e', '<p><strong>Course Overview</strong></p>\n<p>The more deeply a risk manager succeeds in influencing the organisation\'s entire culture, the greater their ability to influence how risks are identified and acted upon. Aligning risk management with general management ensures it becomes part of an organisation\'s core philosophy.</p><p><br></p><p>Embedding Risk Management is a practical two-day workshop on how to set up, enhance or review a risk management framework. You already understand the process of risk management. The next step is to equip yourself to fully integrate business risk and opportunities for innovation into your organisation\'s corporate governance model. Our interactive workshop sessions explore the barriers stifling risk management, and analyse the different solutions. Packed with a series of models you will leave with a toolbox of useful ways of making risk in your organisation more synchronised with how you run your organisation.</p><p><br></p><p><strong>The course charts the 5 stages of the risk management process which are:</strong></p><p><br></p><p>Organisational objectives</p><p>Organisational risk appetite</p><p>Risk evaluation</p><p>Risk treatment</p><p>Risk assurance</p>', NULL, '<p><br></p>', '<p><br></p>', 120000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 06:40:28', '2022-05-12 06:40:28', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661161082/Tmc%20institute/Embedding_Risk_Management_into_the_Corporate_Culture_gxbknr.png', 'Risk Management Courses'),
(32, 'Credit Risk Management', 'c', '<p><strong>Course Overview</strong></p>\n<p>An understanding of credit risk and its management are fundamental in today’s rapidly changing financial services industry. \nThe integrity of the lending process usually depends on sound credit decisions that ensure acceptable risk levels in relation to the expected returns. \nTherefore, officials charged with the responsibility of assessing and advising on credit risk, need to be skilled to make decisions that will have positive \nimpact on the bottom-line of their organisations.</p>', NULL, '<p><strong>TARGET PARTICIPANTS</strong></p>\n<p>This course has been designed for credit, lending, and risk management professionals in the financial institutions.</p>\n<p> Compliance officers, professionals in legal and control departments, as well as, staff of regulatory institutions will also benefit from this course.</p>\n', '<p><strong>LEARNING OUTCOMES</strong></p><p>Develop competency in credit risk management functions</p><p>Apply knowledge of credit risk to minimize incidences of loan default</p><p>Construct a credit risk management framework for their organization</p><p><br></p><p><strong>LEARNING OBJECTIVES</strong></p><p>At the end of the course, participants should be able to:</p><p>Analyse the key elements and drivers of Credit Risk</p><p>Develop an understanding of credit risk management</p><p>Apply credit risk modelling and sensitization techniques</p><p>Analyse the need for capital, differentiating between capital applied by various stakeholders such as management of financial institutions.</p><p>Examine how financial statements can aid in Credit Risk Management</p>', 120000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 06:45:22', '2022-05-12 06:45:22', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661160898/Tmc%20institute/Credit-Risk-Management_1_thvtxs.png', 'Risk Management Courses'),
(33, 'Basel III with Risk Management for Professionals', 'b', '<p><strong>Overview</strong></p><p>Most major banks are now compliant with the</p><p>capital requirements of Basel IIII and are preparing</p><p>– some better than others – for the full impact</p><p>of the imminent liquidity, leverage and long term</p><p>funding source provisions. What next? What will</p><p>be the continuing impact of these changes on the</p><p>capital requirements and, more broadly, on the</p><p>future business strategy of the bank? Did Risk</p><p>Management have a good crisis? Some institutions are re-organising and</p><p>empowering risk management as a result of the</p><p>crisis. Enterprise-wide risk management has been</p><p>a phrase talked about for at least twenty years;</p><p>banks have started to take the concept much</p><p>more seriously – why? How does risk management</p><p>evolve beyond the Accord? Are modelling systems</p><p>up to the challenge? What about Stress Testing?</p><p><br></p><p><strong>Course Objectives</strong></p><p>At  the end of the course each delegate</p><p>will be able to:</p><p>• Make More Meaningful and Complete</p><p>Assumptions</p><p>• Build More Realistic Forecasting Models</p><p>• Understand the Risks Inherent in Your Business</p><p>• Use the Appropriate Measures in Selecting Your</p><p>Forecasting Models</p>', NULL, '<p><br></p>', '<p><br></p>', 120000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 06:49:52', '2022-05-12 06:49:52', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661159520/Tmc%20institute/Basel-III-with-Risk-Management-for-Professionals_qutw1m.png', 'Risk Management Courses'),
(34, 'Introduction to Credit Analysis and Management', 'i', '<p><strong>Overview</strong></p><p>A credit analysis course equips credit professionals with the necessary skills that they need to perform their roles with efficiency. Specifically, the courses equip learners with the skills required to perform due diligence when evaluating the creditworthiness of a potential borrower or debt issuer.</p><p><strong>Course Outline</strong></p><p>1. Principles of Lending</p><p>2. Ratio Analysis</p><p>3. Financial Risk Analysis</p><p>4. Cash Flow Analysis</p><p>5. Projections and Structuring</p>', NULL, '<p><br></p>', '<p><br></p>', 120000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 06:53:34', '2022-05-12 06:53:34', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1652449143/Tmc%20institute/Introduction-to-Credit-Analysis-and-Management_tarnwb.png', 'Risk Management Courses'),
(35, 'Business Continuity & Disaster Recovery Strategies', 'b', '<p><strong>Introduction and Overview</strong></p><p><strong>Defining Business Continuity Management</strong></p><p>Business continuity vs. disaster recovery</p><p>Clarifying the terminology</p><p>The importance of a BCMS</p><p>Overview of ISO 22301</p><p><br></p><p><strong>Running BCMS development as a project</strong></p><p>Obtaining the funding commitments</p><p>Agreeing on critical success factors</p><p>Testing deliverables</p><p><br></p><p><strong>Analyzing The Organizational Context</strong></p><p><strong>Setting goals for the BCMS</strong></p><p>Determining organizational risk appetite</p><p>Defining the operational environment</p><p><br></p><p><strong>Determining the needs of interested parties</strong></p><p>Identifying stakeholders</p><p>Analyzing stakeholder needs</p><p>Mapping stakeholder relationships</p><p><br></p><p><strong>Documenting Business Continuity Requirements</strong></p><p><strong>Identifying mission-critical continuity needs</strong></p><p>Evaluating which functions are critical</p><p>Setting priorities based on time horizons</p><p>Prioritizing processes and applications</p><p><br></p><p><strong>Performing Business Impact Analysis (BIA)</strong></p><p>Identifying threats</p><p>Assessing risks to the enterprise</p><p>Identifying business-critical activities</p><p>Prioritizing infrastructure requirements</p><p><br></p><p><strong>Managing Risks to the Organization</strong></p><p><strong>Characterizing risks</strong></p><p>Defining and identifying the sources of risk</p><p>Choosing a risk assessment method</p><p>Communicating risks across the organization</p><p><br></p><p><strong>Developing appropriate responses</strong></p><p>Matching the response to the risk</p><p>Taking preventive action</p><p>Ensuring appropriate contingencies are in place</p><p><br></p><p><strong>Responding to Incidents</strong></p><p><strong>Creating the incident response plan</strong></p><p>Capturing the planning output</p><p>Creating incident response team charters</p><p>Defining roles and responsibilities</p><p>Responding to incident scenarios</p><p>Information directories and equipment inventories</p><p><br></p><p><strong>Directing the incident response team</strong></p><p>Setting up the command center</p><p>Planning and conducting communications</p><p>Connecting with emergency services</p><p>Team actions following an incident</p><p><br></p><p><strong>Designing Contingency Arrangements</strong></p><p><strong>Establishing a standby site</strong></p><p>Site choices: configuration and acquisition</p><p>Choosing suppliers: in-house vs. third-party</p><p>Specifying equipment</p><p><br></p><p><strong>Selecting backup and restore strategies</strong></p><p>Matching strategy to operational constraints</p><p>Meeting the organization\'s storage requirements for vital records</p><p><br></p><p><strong>Restoring communications and recovering users</strong></p><p>Determining vital users with the BIA</p><p>Rerouting voice, mail, goods delivery</p><p>Eliminating single points of failure</p><p>Connecting end users</p><p>Meeting varied user-recovery needs</p><p><br></p><p><strong>Testing, Rehearsing and Improving Business Continuity Provisions</strong></p><p><strong>Rehearsing business continuity arrangements</strong></p><p>Testing plans with a step-by-step process</p><p>Developing test scenarios and using test results effectively</p><p>Considering the impact of testing on the organization</p><p><br></p><p><strong>Maintaining and improving the BCMS</strong></p><p>Applying change control: why and how</p><p>Ensuring normal developments are accounted for</p><p>Leveraging test results to improve organizational practices</p><p>Managing organizational change</p>', NULL, '<p><strong>Who Should Attend</strong></p>\n<p>This course is valuable for anyone involved in organizational business continuity planning, including those responsible for: </p>\n<p>ICT (Information and Communication Technology) </p><p>Service Delivery,</p>\n<p> Line and project management, </p><p>Facilities, </p><p>Human Resources,</p><p> finance.</p>', '<p><strong>You Will Learn How To</strong></p>\n<p>Implement a Business Continuity Management System (BCMS)</p><p>Set BCM in the organizational context</p>\n<p>Perform Business Impact Analysis (BIA) to identify business priorities</p><p>Coordinate the response following an incident</p>\n<p>Test, maintain, and improve the BCMS</p>', 180000.00, 'naira', NULL, NULL, '3 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 06:59:35', '2022-05-12 06:59:35', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1652449149/Tmc%20institute/Business-Continuity-_-Disaster-Recovery-Strategies_iy7ilt.png', 'Risk Management Courses'),
(36, 'Cyber Security Awareness Training', 'c', '<p><strong>Training Overview</strong></p><p>With more and more businesses being the victims of cyberattacks each year, it is vitally important that organizations are</p><p>able to secure themselves. This means all staff need to be aware of risks and how to reduce them.</p><p>Our Cyber Security Awareness training provides critical training in cyber security threats and how to avoid them. Designed for any</p><p>employee at any organizational level, the training provides easy to follow best practice procedures for all staff to follow in order</p><p>to reduce risk and keep themselves and the company secure. During the training, participants will learn all about the</p><p>cyber security threats they face, how to identify them and how they can help prevent them through simple day to day security</p><p>procedures.</p><p><strong>Cyber Security Awareness Training Outline</strong></p><p><strong>The training covers the following topics:</strong></p><p>Understanding Cyber Threats</p><p>•Hacking</p><p>•Social Engineering</p><p>•Email Phishing</p><p>•Mobile Devices</p><p>•Spyware, Ransomware and Malware</p><p>•Social Media</p><p>•Web Security</p><p>•Physical Security</p><p>•Preventing Cyber Threats</p><p>•Secure Passwords</p><p>•Two-Factor Authentication</p><p>•Securing Mobile Devices</p><p>•Browsing Securely</p><p>• Limiting Removable Media</p><p>•Destroying Sensitive Data</p><p>•Physical Security Measures</p><p>•Preventing Phishing</p>', NULL, '<p><br></p>', '<p><br></p>', 150000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 07:07:18', '2022-05-12 07:07:18', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1652449143/Tmc%20institute/Cyber-Security-Awareness-Training_lki0hb.png', 'Risk Management Courses'),
(37, 'Cyber Security Professional Training for beginners', 'c', '<p><strong>Overview</strong></p><p>Every organization is responsible for ensuring Cyber Security. The ability to protect its information systems from impairment or even theft is essential to success. Implementing effective security measures will not only offer liability protection; it will also increase efficiency and productivity.  With our Cyber Security workshop, participants will understand the different types of malware and security breaches and develop effective prevention methods which will increase overall security. They will also understand the basic concepts </p><p>associated with Cyber Security and what a company needs to stay secure.  </p><p><br></p><p><strong>Module One: Getting Started</strong></p><p> Workshop Objectives  </p><p><strong>Module Two</strong>: <strong>Cyber security Fundamentals</strong></p><p> What is Cyberspace?  </p><p> What is Cyber security?  </p><p> Why is Cyber security Important? </p><p> What is a Hacker? </p><p> Case Study </p><p> Module Two: Review Questions </p><p><strong>Module Three: Types of Malware</strong></p><p> Worms  </p><p> Viruses  </p><p> Spyware  </p><p> Trojans  </p><p> Case Study  </p><p> Module Three: Review Questions </p><p><strong>Module Four: Cyber Security Breaches</strong></p><p> Phishing  </p><p> Identity Theft </p><p> Harassment  </p><p> Cyberstalking  </p><p> Case Study  </p><p> Module Four: Review Questions </p><p><strong>Module Five: Types of Cyber Attacks</strong></p><p> Password Attacks  </p><p> Denial of Service Attacks  </p><p> Passive Attack  </p><p> Penetration Testing </p><p> Case Study </p><p> Module Five: Review Questions </p><p><strong>Module Six: Prevention Tips</strong></p><p> Craft a Strong Password  </p><p> Two‐Step Verification  </p><p> Download Attachments with Care  </p><p> Question Legitimacy of Websites  </p><p> Case Study </p><p> Module Six: Review Questions</p><p><strong>Module Seven: Mobile Protection</strong></p><p> No Credit Card Numbers </p><p> Place Lock on Phone </p><p> Don’t Save Passwords  </p><p> No Personalized Contacts Listed  </p><p> Case Study  </p><p> Module Seven: Review Questions </p><p><strong>Module Eight: Social Network Security</strong></p><p> Don’t Reveal Location  </p><p> Keep Birthdate Hidden  </p><p> Have Private Profile  </p><p> Don’t Link Accounts </p><p> Case Study  </p><p> Module Eight: Review Questions </p><p><strong>Module Nine: Prevention Software</strong></p><p> Firewalls  </p><p> Virtual Private Networks </p><p> Anti‐Virus &amp; Anti‐Spyware  </p><p> Routine Updates  </p><p> Case Study  </p><p> Module Nine: Review Questions </p><p><strong>Module Ten: Critical Cyber Threats</strong></p><p> Critical Cyber Threats </p><p> Cyber terrorism  </p><p> Cyberwarfare  </p><p> Cyberespionage  </p><p> Case Study  </p><p> Module Ten: Review Questions </p><p><strong>Module Eleven: Defense Against Hackers</strong></p><p> Cryptography  </p><p> Digital Forensics  </p><p> Intrusion Detection  </p><p> Legal Recourse  </p><p> Case Study  </p><p> Module Eleven: Review Questions </p><p><strong>Module Twelve: Wrapping Up </strong></p><p> Words from the Wise  </p><p> Review of Parking Lot  </p><p> Lessons Learned  </p><p> Completion of Action Plans and Evaluations  </p>', NULL, '<p><br></p>', '<p><br></p>', 180000.00, 'naira', NULL, NULL, '3 days', NULL, NULL, 1, 'English', 'Lifetime', '2022-05-12 07:12:18', '2022-05-12 07:12:18', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661160970/Tmc%20institute/Cyber-Security-Professional-Training-for-beginners_vkr49i.png', 'Risk Management Courses'),
(38, ' Fraud Awareness Training', 'f', '<p><strong>Areas Covered in the Course</strong></p><p>Red flags of fraud</p><p>Fraud risks and concerns</p><p>Root cause/main cause for fraud and how to prevent it</p><p>Identifying and preventing any fraudulent activities</p><p>Importance of employee fraud awareness training</p><p>Who should be trained </p><p>Who should conduct the training</p><p>When and how often to train</p><p>Raising awareness about fraud hotlines and whistleblower policies</p><p>Basics of effective enterprise-wide fraud detection and prevention</p><p>Mistakes to avoid in setting up a whistleblower hotline</p><p><br></p>', NULL, '<p><br></p>', '<p>Fraud detection and prevention depends on numerous factors — none more significant than tips from employees. However, this is effective only when employees know what to look for. Therefore, to optimize your organization’s employee fraud tip reporting, it is essential for employees to have a robust awareness training on Fraud.</p>', 100000.00, 'naira', NULL, NULL, '1 day', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 07:18:21', '2022-05-12 07:18:21', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1652449148/Tmc%20institute/Fraud-Awareness-Training_xs2ecw.png', 'Financial Crime Prevention'),
(39, 'Compliance and the Management of Compliance Risk ', 'c', '<p>Course Overview\n<p>\n<strong>The basics of Compliance and the Management of Compliance Risk</strong></p>\n<p>Understanding Compliance and the Risk associated with it</p>\n<p>What are important areas in 2022?</p>\n<p>Who are the Compliance Officers’ and Money Laundering Reporting Officers’ Stakeholders?</p>\n<p>Understanding and mitigating the risks of non-compliance</p>\n<p><strong>Creating and Managing a Compliance Risk Management Framework</strong></p>\n<p>Understanding a Risk-Management Approach</p>\n<p>The impact on the business – positively and negatively – of creating a Risk-Management Approach</p>\n<p><strong>Compliance and Corporate Governance</strong></p>\n<p>What is Corporate Governance?</p>\n<p>How does it interact with Compliance?</p>\n<p>Who are the Stakeholders?</p>\n<p>Corporate Governance principles</p><p>Management Committees</p>\n<p>Codes and Guidelines</p><p>Corporate Governance and Financial Crime Prevention</p>\n<p><strong>Compliance Controls and Checks</strong></p><p>What do the Regulators expect?</p><p>Creating a Compliance Monitoring Programme</p><p><strong>Other Financial Crime Considerations</strong></p><p>Money Laundering and Terrorist Financing – a fresh look</p><p>Fraud</p><p>Data Protection and Information Security</p><p>Personal and Sensitive Information</p><p>Data Protection Policies</p><p>Bribery and Corruption</p>', NULL, '<p><br></p>', '<p><br></p>', 180000.00, 'naira', NULL, NULL, '3 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 07:22:05', '2022-05-12 07:22:05', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1652449145/Tmc%20institute/Compliance-and-the-Management-of-Compliance-Risk_lejbfr.png', 'Compliance Courses'),
(40, 'Compliance Officers Masterclass', 'c', '<p><br></p><p><strong>INTRODUCTION</strong></p><p>Compliance Officers Masterclass training course is designed to provide with the knowledge and skills needed to understand and support regulatory compliance and enterprise wide risk management. Also, to know how to deal with the rules, the markets, products and transactions, to promote best practices and international standards that align with business and regulatory requirements.</p><p><br></p><p><strong>OBJECTIVES </strong></p><p>Understand the objectives &amp; roles of regulation</p><p>Understand the responsibilities of compliance</p><p>Develop a good understanding of compliance principles</p><p>Understand the concept of Compliance and the efforts being made to manage the risk related to it</p><p>Have the skills required to approach compliance and its related functions with confidence</p><p>Identify and understand compliance risks, and the consequences of noncompliance, to mitigate effectively the risk of compliance breaches</p><p>Develop an appropriate Compliance Manual</p><p>Create a compliance culture</p><p>Protect the organization’s reputation</p><p>The relationship between Compliance and Corporate Governance</p><p>Develop compliance programs to fight money laundering practices, and to apply Corporate Governance rules</p><p>Develop best strategies to help managers and prevent risk within your firm</p><p><br></p><p><strong>DAY 1</strong></p><p>The Regulatory Environment and the Role of Regulators </p><p>An Overview of the Regulatory Environment</p><p>The Objectives of Financial Services Regulation</p><p>International Regulation</p><p>The Role of the Regulators</p><p>What do the Regulators expect?</p><p>Your Jurisdiction Environment</p><p>The Best Practice in Regulatory Obligation</p><p>ISO 19600: Compliance Management Systems</p><p><strong>DAY 2</strong></p><p>The Compliance Functions  </p><p>Compliance Structure</p><p>The Role of the Board of Directors, the Supervisors, the Internal and External Auditors</p><p>Responsibilities of the Compliance Officer</p><p>Key Compliance Activities and Processes</p><p>Compliance and Corporate Governance</p><p>Corporate Governance and Financial Crime Prevention</p><p>Compliance Training</p><p>Conflicts of Interest</p><p>Ongoing Compliance with Laws and Regulations</p><p><strong>DAY 3</strong></p><p>Risk Management and Compliance Risk </p><p>Understanding a Risk Management Approach</p><p>Creating a Risk Management Approach</p><p>The Definition of Governance, Risk and Compliance</p><p>The Need for Internal Controls</p><p>Identify, Mitigate and Control Risks Effectively</p><p>Approaches to Risk Assessment</p><p>The Importance of Compliance Culture</p><p>Managing Key Relationships – external and internal</p><p><strong>DAY 4</strong></p><p>Establishing an Effective Compliance Function </p><p>Factors to be Considered in Designing a Compliance System</p><p>Developing an Internal Compliance System</p><p>Implementing and Communicating Internal Compliance Arrangement</p><p>Compliance Manual</p><p>Establishing Policies and Procedures</p><p>Creating a Compliance Monitoring Programme</p><p>The Need for Independence</p><p>Record Keeping ƒ Compliance Reports</p><p>Managing the Regulators and Change in Regulations</p><p><strong>DAY 5</strong></p><p>Managing the Risk of Money Laundering and Financial Crimes</p><p>Understanding Money Laundering Offences</p><p>MLRO and Compliance Officer</p><p>The Risk-based Approach to Money Laundering / Terrorist Financing</p><p>Suspicious Activity Monitoring, Detection and Reporting</p><p>Preventing Fraud</p><p>Bribery and Corruption</p><p>Insider Trading</p><p>Market Abuse</p><p>Sanctions and their Role in the Global Economy</p>', NULL, '<p>Compliance Officers / Managers and their Deputies</p><p>Managers with responsibility for internal compliance controls</p><p>Legal, Audit, Risk, Operations and IT Specialists</p><p>Risk Managers</p><p>Staff working in risk functions</p><p>Internal &amp; External Audit Staff</p><p>Regulatory Representatives</p><p>AML Officers</p><p>Accountants</p><p>Lawyers</p><p>All Staff working in or aspiring to work in the compliance area</p>', '<p><br></p>', 300000.00, 'naira', NULL, NULL, '5 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 07:27:49', '2022-05-12 07:27:49', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1652449147/Tmc%20institute/Compliance-Officers-Masterclass_lvzpqg.png', 'Compliance Courses'),
(41, 'Regulatory Visits Masterclass', 'r', '<p><strong>Course OverView</strong></p>\n<p>Day 1:  Regulatory Relations – context and expectations</p>\n<p>Day 2:  Regulatory Visit Management -Training to the Test (Part 1)</p><p>Day 3:  Regulatory Visit Management -Training to the Test (Part 2)</p>', NULL, '<p><br></p>', '<p><br></p>', 125000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 07:32:26', '2022-05-12 07:32:26', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1652449148/Tmc%20institute/Regulatory-Visits-Masterclass_ikghdo.png', 'Compliance Courses'),
(42, ' Anti-Bribery & Corruption ', 'a', '<p><strong>Introduction</strong></p><p>Doing business globally has opened up organizations to new risks and rewards. Among the most pressing risks facing organizations today is the threat of bribery and corruption. Organizations that proactively work to educate and prevent corruption can increase compliance, reduce the likelihood of misconduct and avoid prosecution for missteps.</p><p><br></p><p><strong>Outline</strong></p><p>Fundamentals of policy and the law</p><p>Impact of corruption on the organization</p><p>Risks associated with improper payments</p><p>Individual employee responsibilities and obligations</p><p>Overview of gifts, hospitality and reimbursement of business expenses</p><p>Improper payments under the law</p><p>Consequences of violating law or policy</p><p>Spotting and reporting conduct that may violate law or policy</p><p>Critical nature of seeking guidance and asking questions</p><p>Importance of maintaining accurate books and records</p><p>Spotting common red flags and suspicious activity</p><p>Prohibition of retaliation</p>', NULL, '<p><br></p>', '<p><br></p>', 120000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 07:37:44', '2022-05-12 07:37:44', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1652449148/Tmc%20institute/Anti-Bribery-_-Corruption_kept5l.png', 'Compliance Courses'),
(43, 'High Impact Leadership for Senior Executives', 'h', '<p><strong>Course Overview</strong></p>\n<p>MODULE I: Leading for Strategic Success</p><p><br></p><p><strong>DAY 1</strong></p><p>The Current Context of Leadership</p><p>Definitions of Leadership</p><p>Double Loop Learning</p><p>Rational and Non-rational Approaches</p><p>Leadership of Groups and Organisations</p><p>Transformational Change</p><p><strong>DAY 2</strong></p><p>Alignment: Linking Strategy to Operations</p><p>Overview of a Strategic Management System</p><p>Measuring Results</p><p>Foundations for Success: Strategic Leadership</p><p>Business Process Management Concepts</p><p>Describing and Realizing improvements using Process Models</p><p><strong>DAY 3</strong></p><p>Analysing and Solving Complex Problems</p><p>The Difference between Simple and Complex Problems</p><p>Understanding Complexity</p><p>Solving Complex Problems with Systems Thinking</p><p>Creative Thinking Techniques</p><p>Analysing, Preparing and Winning Arguments</p><p><strong>DAY 4</strong></p><p>Planning for Success</p><p>The Difference between Good Strategy and Bad Strategy</p><p>Making a Business Case</p><p>Group Decision Making and Change</p><p>Evidence-based Management – what really works?</p><p>Case Study Briefing</p><p><strong>DAY 5</strong></p><p>Overcoming Execution Problems – Useful Tools and Techniques</p><p>Managing Human Factors in Business Performance</p><p>Test your Leadership – practical case study</p><p>Overcoming Bias and Understanding Psychology to Get Results</p><p>Individual Plans and Presentations</p><p>Commitment to Action</p><p> </p><p><strong>MODULE II: Leading for Organisational Impact</strong></p><p><br></p><p><strong>DAY 6</strong></p><p>Leaders of the Future</p><p>Not all Managers are Leaders</p><p>The Purpose of Leadership</p><p>A New View of Leadership: The Role of Coach and Mentor</p><p>Leadership Styles: The Importance of Context</p><p>The Impact of Leadership on Performance</p><p>Essential Ingredients of a High-performance Organization</p><p><strong>DAY 7</strong></p><p>Hiring, On-boarding and Training Future Leaders</p><p>Attracting The Best Talent: Benchmark Practices</p><p>Understanding Future New-hires: Welcome to The Age of Millennials</p><p>Creating a Values-based rather than a Policies-based Environment</p><p>New Approaches to On-boarding</p><p>The Biochemical Basis of Productive Employees</p><p>Designing and Benefiting from an Engaged Workforce</p><p>Finding and Hiring The Best Talent</p><p>Spotting and Avoiding the Narcissist</p><p><strong>DAY 8</strong></p><p>Leveraging Talent</p><p>Developing Trust: The Foundation of Engaging Employees in Setting Direction</p><p>Filling Employee’s Psychological Bucket</p><p>Driving Improved Performance by Applying The Pygmalion Principal</p><p>Improved Performance Management Practices</p><p>Boosting Performance through Raised Expectations</p><p>Coaching and Mentoring that Boosts Confidence, Relationships and Performance</p><p>Conducting Robust Discussions that Uncover The Root Cause and Solutions of Vexing Issues</p><p><strong>DAY 9</strong></p><p>Powering Your Team to High Performance</p><p>New Approaches to Teamwork</p><p>Elements of Effective Teams</p><p>Decision-Making: Choosing the Best Strategy to Suit Your Circumstances</p><p>Achieving Alignment with The Corporate Vision and Mission</p><p>Changing Your Measurement System to Build Commitment and Enthusiasm</p><p>Identifying a “balanced” Set of Key Performance Indicators</p><p>Recognition and Rewards</p><p><strong>DAY 10</strong></p><p>Leading the Charge!</p><p>Becoming an Agent of Change</p><p>Alternative Strategies to Deal with Conflict</p><p>Choosing The Best Approach for Your Situation</p><p>Building A Model for Influencing Change: Lessons from The Eagle</p><p>Understanding and Leveraging Your Power Base</p><p>The Power of Humility</p><p>Getting into The Head and Heart of those who are Intransigent</p><p>Producing Win-Win Outcomes</p>', NULL, '<p><br></p>', '<p><br></p>', 500000.00, 'naira', NULL, NULL, '5 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 08:54:40', '2022-05-12 08:54:40', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661161358/Tmc%20institute/High-Impact-Leadership-for-Senior-Executives_fyu0cl.png', 'Corporate Governance'),
(44, 'Executive Secretary Masterclass', 'e', '<p><strong>The Course Content</strong></p><p><strong>Day One:</strong> Making an Impact through Excellent Communication Skills </p><p>Building Your Confidence</p><p>Developing assertiveness without causing offence</p><p>Understanding and working with different Communication styles</p><p>How to influence through non-verbal communication</p><p>Effective Listening and Questioning</p><p>Expanding your influence and effectiveness</p><p><strong>Day Two:</strong> Blending with the Executive Team</p><p>Understanding and working with different working styles</p><p>Understanding Team dynamics</p><p>The fundaments of making an impact</p><p>Delegation, feedback and building a motivated team</p><p>Problem Solving and making the best decisions</p><p>Using Coaching and Mentoring to inspire and motivate</p><p><strong>Day Three:</strong> Refining your Role &amp; Increasing your Management Potential</p><p>Understanding the basics of performance management</p><p>Running effective meetings</p><p>Effective Time Management</p><p>When things go wrong – how to resolve conflict</p><p>Refining your Influencing skills</p><p>Expanding your toolbox of management skills</p><p>Developing emotional intelligence and resilience at work</p><p><br></p>', NULL, '<p><br></p>', '<p><br></p>', 200000.00, 'naira', NULL, NULL, '3 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 08:59:14', '2022-05-12 08:59:14', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661161194/Tmc%20institute/Executive-Secretary-Masterclass_gls78q.png', 'Corporate Governance'),
(45, 'Company Secretary Masterclass', 'c', '<p><strong>Course Overview</strong></p>\n<p>The executive team and in particular the Company Secretary and the General Counsel provide a key role in supporting the Board of any organisation. This 4-day course is intended for those who need to understand the breadth and scope of the role and responsibilities of both Directors and those who support boards and in particular Company Secretaries and In-House Counsel. The course will explore the role of the Company Secretary which continues to evolve as the role is now about being: an active participant with the board and directors to ensure good corporate governance and board effectiveness; an advisor to the board to ensure that board intentions and policies are aligned and functioning correctly; a gatekeeper to ensure that laws are not broken and that policies are complied with; and an enabler and provider of information on trends and new developments. </p><p><br></p><p><strong>Day 1</strong> </p><p>The Role and Responsibilities of the Company Secretary and those Providing Board Support</p><p>What does an Effective Company Secretary do?</p><p>Case study: The diverse role of the Company Secretary. The Role of the Company Secretary in Different Jurisdictions. Diversity of practices and norms and cross-border complexity.</p><p>What are the Responsibilities of the Company Secretary? Responsibilities to the board, the company and shareholders</p><p>Core duties.</p><p>Additional duties that may come with the role</p><p>Structure of the secretariat and combining the role with other functions.</p><p>Case study: Looking at the responsibilities of the Company Secretary, what are the Compliance Functions of the Company Secretary? </p><p><br></p><p><strong>Day 2</strong></p><p>Supporting Boards: Board Meetings, General Meetings and Information Flows </p><p>How to Get the Most Out of Meetings</p><p>The annual calendar of board business and the forward planner </p><p>Meeting frequency, Meeting duration, Attendance issues</p><p>The purpose of board packs and confidentiality aspects </p><p>Ensuring board papers are ‘fit for purpose’ to facilitate good decision making </p><p>Using IT effectively in board processes </p><p>Minutes of meetings and minute logs </p><p>Document retention and document retention policies </p><p>Case study: Examining agenda issues. Common Problem Areas in Meetings. Roles and responsibilities. Time management. Meeting behaviours. </p><p><br></p><p><strong>Day 3</strong></p><p>Supporting Boards and the Organisation by Improving Corporate Governance </p><p>The Company Secretary and the Governance Framework</p><p>The Company Secretary and the overall governance architecture of the organisation </p><p>The Company Secretary as a ‘gatekeeper’ Who owns the governance framework of an organisation </p><p>The Company Secretary as a Conduit of Information to the Board </p><p>The link between the board and the business </p><p>Being a point of access, services and advice for all directors </p><p>Responsibility for all board procedures and the compliance of board procedures </p><p>The Company Secretary and the Board Working together with the Chairman on governance issues </p><p>Advising and assisting Directors on their duties and responsibilities </p><p>Promoting and reviewing new corporate governance developments </p><p>Acting as a communicator with Non-Executive Directors </p><p>Role play: Investigating the relationships between the Chairman and Chief Executive and the Company Secretary </p><p>The Company Secretary: Induction and Training Role in facilitating director induction and professional development </p><p>The Company Secretary and the Executive </p><p>Effective management of the conflicts inherent in the role </p><p>Managing the impacts of additional duties and exploring other duties</p><p>Role play: Dealing with dominant Executive Directors </p><p>The Company Secretary and Board and Committee </p><p>Case study: To externally evaluate or not? Examining the Company Secretary’s role </p><p><strong>Day 4</strong> </p><p>How to Guide and Influence The Company Secretary: Directing the Focus of the Board </p><p>Forward planner/board paper tracking schedules/use of templates </p><p>Effective use of pre-meetings </p><p>Enhanced communication and follow-up </p><p>Use of external independent advice Reviewing past decisions</p><p>Managing change Managing crisis </p><p>Case study: The importance of external sources of advice and how to maximise benefits </p><p>Working with Directors </p><p>The Company Secretary’s role in decision making </p><p>Role play: Meetings with Executives and Non-Executive Directors and getting the most out of them </p><p>The Company Secretary and Shareholders Shareholder communications and documents: notice of AGM and proxy forms and communications with shareholders </p><p>Ascertaining who the shareholders actually are and shareholder monitoring issues: who actually are the owners?</p><p>Board support and investor relations </p><p>Stewardship Code considerations and enhanced engagement opportunities </p><p>Case study: Minority shareholder abuse and handling investor relations </p><p>Skills of the Company Secretary </p><p>What qualities are needed </p><p>Mandate of the Company Secretary </p><p>Potential to narrow or expand </p><p>Perception of the Company Secretary </p><p>Strategic v administrative </p><p>Potential to be seen as uncommercial </p><p><br></p>', NULL, '<p><br></p>', '<p><br></p>', 300000.00, 'naira', NULL, NULL, '4 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 09:02:56', '2022-05-12 09:02:56', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661160543/Tmc%20institute/Company-Secretary-Masterclass_nvmmof.png', 'Corporate Governance');
INSERT INTO `course_infos` (`id`, `coursename`, `firstletter`, `coursedetails`, `certification`, `whothiscoursefor`, `learning`, `price`, `currency_name`, `instructor`, `rating`, `duration`, `lesson`, `quizzes`, `certificate`, `language`, `access`, `created_at`, `updated_at`, `picture`, `MainHead`) VALUES
(46, 'Corporate Governance, Business Ethics and Corporate Social Responsibility (for Senior Management Staff)', 'c', '<p><strong>Course Overview</strong></p>\n<p>The couse aims to develop an understanding of the underlying concept of corporate governance, business ethics and CSR which are relevant to the contemporary business environment. The course further aims to develop an ability to critically analyse ethical issues in organisations.</p>\n<p><strong>        Course Outline</strong></p><ul>\n<li>Introduction</li><li>Corporate Governance Concept and Theories</li>\n<li>Model of Corporate Governance</li>\n<li>Board of Directors</li>\n<li>Corporate Governance Issues in Practice</li>\n<li>Introduction to Ethics</li><li>Ethics Resource Management</li>\n<li>Employer and Employees</li><li>Globalization &amp; Human Rights</li>\n<li>Customer Rights and Privacy</li><li>Environmental Issues</li>\n<li>Law &amp; Regulation on Ethical Issues</li><li>Ethical Reasoning and Corporate Programs</li>\n<li>Ethiics in Finance </li></ul><p><br></p><p> </p>', NULL, '<p><br></p>', '<p><br></p>', 200000.00, 'naira', NULL, NULL, '3 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 09:08:25', '2022-05-12 09:08:25', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661160842/Tmc%20institute/Corporate-Governance_-Business-Ethics-and-Corporate-Social-Responsibility-_for-Senior-Management-Staff_rgorng.png', 'Corporate Governance'),
(47, 'Board Leadership Series', 'b', '<p><strong>Course Overview</strong></p><p>This training provides participants with the professional knowledge, tools and frameworks to boost Board’s performance.</p><p>Recognising that the Board of Directors plays a critical role in the organisation’s success, the goal of this program is to examine all issues impacting the Board performance and the effectiveness of its strategic operations<strong>.</strong></p><p>This training will discuss in details all aspects related to the functional roles as well as the duties and responsibilities of the different Board players and will also emphasize the importance of the boardroom dynamics and the vital impact of the Board and company leadership in setting a positive culture.</p><p>This training will also address the importance of having a regular evaluation for the Board performance with an aim to detect areas for improvements and implement changes necessary to become more effective.</p><p><br></p><p><strong>Course Objectives</strong></p><p>Understand the Roles, Responsibilities and Duties of a Board</p><p>Discuss Board Formal Offices, Composition and Structure</p><p>Learn about Board Instruments, Tools and Procedures</p><p>Discuss how to run effective Board Meetings</p><p>Study the Board Governance Framework</p><p>Understand The GRC (Governance, Risk and Compliance) Perspective</p><p>Identify the Board Effectiveness Challenges</p><p>Know about Board Evaluation Practices</p><p><br></p><p><strong>Who should Attend</strong></p><p>Chairs, Committee Chairs</p><p>Board Members, Committee Members</p><p>Board Secretaries</p><p>CEOs, Executive Directors and Senior Management</p><p>Officers in charge of the Governance framework within the Organisation</p><p><br></p><p><strong>Course Outline</strong></p><p>Roles , Duties and Responsibilities of a Board</p><p>The Strategic Planning Role of a Board</p><p>Board Involvement Models</p><p>Board Composition &amp; Structure</p><p>Board Instruments and Tools</p><p>Board Meetings</p><p>Board Governance Framework</p><p>Characteristics of a High Performance Board ( HPB )</p><p>The Governance, Risk and Compliance ( GRC ) perspective</p><p>Enterprise Risk Management (ERM)</p><p>The Board Effectiveness Framework</p><p>Impact of Informal Board’s “Culture”</p><p>Influence of Leadership of  the Board and the Organisation</p><p>Board Evaluation &amp; Board Evaluation Framework</p>', NULL, '<p><br></p>', '<p><br></p>', 400000.00, 'naira', NULL, NULL, '3 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 09:11:15', '2022-05-12 09:11:15', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661160211/Tmc%20institute/Board-Leadership-Series_qppc4g.png', 'Corporate Governance'),
(48, 'Problem Loans: Warning Signals, Assessment & Recovery', 'p', '<p><strong>Course Overview</strong></p><p>This course covers the key factors that cause companies to become financially distressed. It emphasizes the various early warning signs that lenders must be aware of and the importance of quickly identifying companies that are having problems to limit value destruction and improve recovery rates for lenders. Signs include understanding how and when cash flows become distressed, as well as measuring and assessing liquidity through a downturn. This course discusses the roles of covenants and collateral, including how they can fail to protect lenders. Finally, the course works through the measures that must be taken to resolve financial distress.<strong> </strong></p><p><br></p><p><strong>Course Outline</strong></p><p>Cash flow, cash cycle, and sustainable growth</p><p>Monitoring the credit cycle</p><p>Portfolio monitoring and analysis</p><p>Early warning signs</p><p>Loan classifications and re-rating</p><p>Stay or leave analysis</p><p>Remedial management</p><p>Workout planning, negotiation and execution</p><p>Parent guarantees</p><p>Analysis and execution ofcollateral and guarantees</p><p>Managing third parties (appraisers, lawyers, auctioneers,etc.) </p>', NULL, '<p><br></p>', '<p><br></p>', 200000.00, 'naira', NULL, NULL, '3 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 09:15:51', '2022-05-12 09:15:51', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661162187/Tmc%20institute/Problem-Loans--Warning-Signals_-Assessment-_-Recovery_ybrxej.png', 'Corporate Governance'),
(49, 'Basic Credit Analysis and Management', 'b', '<p><strong>Course Outline</strong></p><p>Lending activities is one of the major source of revenue for lending/financial institutions as intermediaries in the financial market. Credit risk assets also pose the greatest danger to the survival or existence of any lending institution. It is therefore imperative that all lending officers and risk managers in the institution are equipped with the technical and analytical skills required to analyze credit risks (identify, quantify, mitigate, and manage risks).</p><p>This training is comprehensively designed to strengthen the capacity of all participants to understand credit policy issues, appraise credit requests, analyze various credit risks, package and process loans, and effectively manage the bank’s loan portfolio in their assigned business areas.</p><p>It is also guaranteed to deepen and widen the skills and capabilities of participants to understand and analyze the inherent and imminent risks posed by activities or events or changes in the macro-economy (global, regional, and national), and the industry environment on borrowing firms; and hence, be better able to mitigate and manage such risks. </p><p><br></p><p><strong>Course Outline:</strong></p><p>Bank Lending and the Related Risks</p><p>Overview of Credit Risk Analysis</p><p>Understanding Credit Policy Guides </p><p>Macroeconomic, Industry, and Business Specific Risks Analysis for Lenders</p><p>Evaluating Management Capacity</p><p>Analyzing Customer Banking Relationships</p><p>Financial Risks Analysis – Cracking the Ratios.</p><p>Working Capital Analysis</p><p>Mastering Loan Structuring Loan Types and Appropriate Collateral Types.</p><p>Credit Administration, Processing, and Disbursement</p><p>Loan Monitoring and Post-Disbursement Tips Parting Shots!</p>', NULL, '<p>Business Leaders</p><p>Credit Analysts and Administrators</p><p>Credit Approval Officers</p><p>Relationship Managers</p><p>Account / Relationship Officers</p>', '<p><br></p>', 200000.00, 'naira', NULL, NULL, '3 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 09:20:48', '2022-05-12 09:20:48', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661159582/Tmc%20institute/Basic-Credit-Analysis-and-Management_dwhkpa.png', 'Corporate Governance'),
(50, 'Anti-Money Laundering Compliance/ Combatting Terrorism Finance', 'a', '<p><strong>Course Overview</strong></p><p>This Anti-Money Laundering (AML) Compliance Course is designed to develop the essential skills necessary to enable you to understand how to support your company in Anti-Money Laundering and countering terrorist finance and how to prevent, detect, and report money laundering activities.</p><p><br></p><p>This training course covers regulatory environment and focus on the importance of Know your Customer (KYC), the importance of Customer Due Diligence (CDD), sanctions monitoring and the role of the Money Laundering Reporting Officer in practical terms. You will learn about studying the AML risks involved in emerging industry sectors.</p><p><br></p><p><strong>Course Outline</strong></p><p><strong>Anti-Money Laundering (AML) and Countering Terrorist Financing (CTF)</strong></p><p>Why the Current Focus on Anti-Money Laundering (AML) / Countering Terrorist Financing (CTF)?</p><p>Money Laundering &amp; the Financing of Terrorism Definitions</p><p>Differences and Similarities between Money Laundering and Terrorist Financing</p><p>Key Stages of Money Laundering</p><p>Typologies &amp; Techniques of Money Laundering</p><p>Indicators of Money Laundering</p><p>Products and Services Vulnerable to Money Laundering or Terrorism Financing</p><p>Money Laundering Trends in (Jurisdiction)</p><p><br></p><p><strong>The International Organizations of Anti-Money Laundering (AML) / Countering Terrorist Financing (CTF)</strong></p><p>The International bodies’ response to Anti-Money Laundering (AML) and  Countering Terrorist Financing (CTF)</p><p>The International Measures to Prevent Money Laundering and Financing of Terrorism </p><p>The Complex and Interrelated Risks with Respect to Money Laundering and Sanctions</p><p>FATF and Its Roles of Anti-Money Laundering (AML)</p><p>Customer Policies and Procedures</p><p>International Legislation and Legislation Applicable to Your Jurisdiction</p><p>The Wider Context for Financial Crime Risk and Regulation in which Your Firm Operates</p><p>The Role and Responsibilities of (Front / Back Office / AML / Compliance Officer), and How these fit within the financial crime risk-management life cycle?</p><p><br></p><p><strong>Compliance Strategie</strong>s</p><p>Jurisdiction Anti-Money Laundering (AML) and  Countering Terrorist Financing (CTF)</p><p>Laws and Regulations</p><p>The Requirements for Robust Anti-Money Laundering Operations, Internal Controls, Procedures and Policies</p><p>Cooperation with the Authorities and the Regulators</p><p>Know Your Customer (KYC) and ID&amp;V Rules</p><p>Tipping-off</p><p>Market Abuse and Insider Dealing</p><p>Client Relations and Human Resources Strategies</p><p>The Relevant Laws and Regulations in Your Jurisdiction, The Internation</p>', NULL, '<p><br></p>', '<p><br></p>', 200000.00, 'naira', NULL, NULL, '3 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 09:23:48', '2022-05-12 09:23:48', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661159436/Tmc%20institute/Anti-Money-Laundering-Compliance--Combatting-Terrorism-Finance_hyx6rc.png', 'Financial Crime Prevention'),
(51, 'Know Your Customer Specialization Course', 'k', '<p><strong>Course Overview</strong></p><p>Know your customer (KYC) and customer due diligence (CDD) training helps you understand how to mitigate the AML risks associated with customer acquisition and retention activities.</p><p><br></p><p><strong>Course Outline</strong></p><p>Customer Identification</p><p>Customer Due Diligence &amp; Enhanced Due Diligence</p><p>Continuous Monitoring</p>', NULL, '<p><br></p>', '<p><br></p>', 125000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 09:26:46', '2022-05-12 09:26:46', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661161494/Tmc%20institute/Know-Your-Customer-Specialization-Course_fxqxsk.png', 'Financial Crime Prevention'),
(52, 'Compliance Program for Anti–Money Laundering', 'c', '<p><strong>Course Outline</strong></p><p>What is an AML compliance program?</p><p>What does a business have to do to stay AML compliant?</p><p>Factors that impact AML compliance</p><p>How to develop an AML program</p><p>The role of an AML compliance officer </p><p>Appointing an AML compliance officer</p>', NULL, '<p><br></p>', '<p><br></p>', 125000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 09:29:41', '2022-05-12 09:29:41', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661160733/Tmc%20institute/Compliance-Program-for-Anti_Money-Laundering_bdncj0.png', 'Financial Crime Prevention'),
(53, 'Money Laundering Detection Techniques', 'm', '<p><strong>Course Outline</strong></p><p>Prevention, Detection and Due Diligence</p><p>Early-Warning Mechanisms</p><p>Designing Money Laundering and Terrorist Financing Risk Assessments</p><p>Applying Know Your Customer (KYC) &amp; Customer Due Diligence (CDD )</p><p>Risk Management of Politically Exposed Persons (PEPs)</p><p>Screening</p><p>Best Practices Applicable to Firms / Banks </p><p>Sanctions</p>', NULL, '<p><br></p>', '<p><br></p>', 125000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 09:32:07', '2022-05-12 09:32:07', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661161796/Tmc%20institute/Money-Laundering-Detection-Techniques_ba6qwa.png', 'Financial Crime Prevention'),
(54, 'Recognizing and Reporting Suspicious Transactions', 'r', '<p><strong>Course Outline</strong></p><p>Statutory Obligations</p><p>Identifying Suspicious Transactions</p><p>Internal and External Reporting of Suspicious Transactions</p><p>Identifying High-risk Indicators - Understand what to do with the information and whom to escalate it to</p><p>The Role of the MLRO</p><p>The Importance of Record Keeping</p><p>Responsibilities of Firms / Banks and Individuals</p><p>Setting-up Monitoring Procedures</p>', NULL, '<p><br></p>', '<p><br></p>', 125000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 09:36:02', '2022-05-12 09:36:02', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661162254/Tmc%20institute/Recognizing-and-Reporting-Suspicious-Transactions_u9b015.png', 'Financial Crime Prevention'),
(55, 'Compliance Governance & Risk Management in Insurance', 'c', '<p><strong>Course Overview</strong></p><p>The financial sector has become more and more challenging in recent times with the country working closer with the global community. Insurance professionals, like their counterparts in the financial sector, have to be on their toes to keep their knowledge levels about changing regulatory requirements updated. Those involved in governance and compliance functions need to know this as part of their job requirement. However, to perform efficiently and to ensure that the spirit of the regulations is not lost under the monotony and routine nature of mandatory compliances, they need to appreciate the philosophy behind the regulations, widen their awareness of international trends and develop perspectives to ensure proper application of their knowledge to local realities.</p><p><br></p><p><strong>Course Outline</strong></p><p>Understanding the conceptual framework of insurance regulation,</p><p>Awareness of the international regulatory scenario,</p><p>Statutory provisions contained in various legislations applicable in the country,</p><p>Specific regulations drawn by the insurance regulator, </p><p>Market conduct,</p><p>In-depth learning of the various compliances required in the Insurance Sector</p>', NULL, '<p><br></p>', '<p><br></p>', 120000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 09:38:12', '2022-05-12 09:38:12', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661160634/Tmc%20institute/Compliance-Governance-_-Risk-Management-in-Insurance_m5q1k0.png', 'Insurance Courses'),
(56, 'Internal Audit for Insurance', 'i', '<p><strong>Course Objectives</strong></p><p>By the end of the course, participants will be able to:</p><p>• Define the scope and function of internal audit within the company</p><p>• Distinguish the types of internal audit assignments related to operational, compliance, quality, safety or financial internal audit</p><p>• Describe the internal audit planning guidelines and develop a risk-based audit plan</p><p>• Apply techniques for risk identification, controls identification and controls testing</p><p>• Identify the best sampling techniques in an internal audit assignment considering sample size or sample selection</p><p>• Select the appropriate internal audit test tools and list the advantages and disadvantages of each</p><p><br></p><p><strong>Course Outline</strong></p><p><br></p><p>• Risk and internal audit overview</p><p>o Definition of risk</p><p>o Types of risk in an organization</p><p>o Strategic, reporting, compliance, operational, financial and physical</p><p>o Scope of corporate governance</p><p>o Building blocks of corporate governance</p><p>o Internal audit as a function of corporate governance</p><p>o Scope of internal auditing</p><p>o Reasons to have an internal audit function</p><p>o Distinguishing internal from external auditing</p><p>o The internal audit charter</p><p>o The role of an audit committee</p><p>o Risk management process</p><p>o Five steps to accomplish an effective Enterprise Risk Management (ERM) framework</p><p>o COSO enterprise risk management framework</p><p>o Determining the risk appetite</p><p>o The role of an internal audit activity in risk management</p><p>• Types of internal auditing</p><p>o Assurance services: the third line of defense</p><p>o Performance auditing </p><p>o Operational auditing</p><p>o Contracts auditing</p><p>o Financial auditing: accounting cycles audited by the internal audit function</p><p>o Security and privacy auditing</p><p>o Quality auditing</p><p>o Compliance auditing</p><p>• Risk-based internal audit: planning the fieldwork</p><p>o Reasons for risk-based audit planning</p><p>o Three stages for implementing risk-based internal audit</p><p>o Stage 1: risk maturity assessment</p><p>o Actions of internal audit to assess risk maturity</p><p>o Overall audit strategy based on risk maturity</p><p>o Conclusion on risk management framework</p><p>o Stage 2: production of the audit plan</p><p>o Assurance requirements from board and management</p><p>o Actions to achieve production of an audit plan</p><p>o Identify processes and responses on which assurance is required</p><p>o Categorize and prioritize the risks</p><p>o Scoring and weighing risks</p><p>o Link risks to audit assignments</p><p>o Using assurance maps to determine assurance requirements</p><p>o Stage 3: conducting audit engagements</p><p>• Risk-based internal audit: conducting audit engagements</p><p>o Internal audit role in performing the audit</p><p>o Assessing risks: inherent, control, detection and audit risks</p><p>o How management and internal audit can minimize risk</p><p>o Engagement planning</p><p>o Engagement objectives, scope and criteria</p><p>o Engagement work program</p><p>o Role of internal audit staff</p><p>o Defining management assertions</p><p>o Uncover risks during audit engagement</p><p>o Example of internal audit risk assessment scale</p><p>o Testing management controls</p><p>o Insights on flowcharting for understanding cycles and controls</p><p>o Assess design of internal controls</p><p>o Test operating effectiveness of internal controls</p><p>o 10 steps to complete the audit stage</p><p>o Summarizing audit conclusions for the audit committee</p><p>• Technical tools for internal auditors</p><p>o Tips and tools for audit sampling</p><p>o Information gathered by internal auditors</p><p>o 4 qualities of information</p><p>o Sources and nature of information</p><p>o Assessing the degree of persuasiveness</p><p>o Types of engagement procedures</p><p>o 15 internal audit test tools</p><p>o Observation</p><p>o Interviewing: a disliked technique</p><p>o Interviewing skills: how to run a successful interview</p><p>o Role play: internal auditor in action</p><p>o Examining records</p><p>o Verification and confirmations</p><p>o Vouching and tracing</p><p>o Re-performing</p><p>o Internal audit working papers</p><p>o Best practices for managing working papers</p><p>o Retention policies</p><p>o Communicating fieldwork results and recommendations</p><p>o Legal considerations for communicating results</p><p>o 4 attributes of an observation or recommendation</p><p>o Disseminating results and exit meetings</p><p><br></p>', NULL, '<p><br></p>', '<p><br></p>', 120000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 09:41:04', '2022-05-12 09:41:04', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661161432/Tmc%20institute/Internal-Audit-for-Insurance_voflph.png', 'Insurance Courses'),
(57, 'Marketing and Sales for Insurance', 'm', '<p><strong>Overview</strong></p><p>We provide Insurance Sales &amp; Marketing Training to assist participants to address any gaps in their required skills. Whether you require common insurance broker sales training, insurance sales training, or life insurance sales training, our method to insurance sales training is much easy to take note and learn before you speak and sell. In this highly interactive insurance sales training, you will study how to ask your clients the right questions and listen to their needs.</p><p><br></p><p><strong>Course Outline</strong></p><p>Selling</p><p>Strategic account management </p><p>Negotiation</p><p>Mastering the Opportunity</p><p>How to sell professional services</p><p>Sales Coaching</p><p>Sales Management</p><p>Sales objection Management</p><p>Motivational Training</p><p>Body Language</p>', NULL, '<p><br></p>', '<p><br></p>', 120000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 09:43:20', '2022-05-12 09:43:20', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661161651/Tmc%20institute/Marketing-and-Sales-for-Insurance_eizwrr.png', 'Insurance Courses'),
(58, 'Underwriting (Life & General) for Insurance', 'u', '<p><strong>Objectives</strong></p><p>This course intends to provide a basic understanding of the insurance mechanism. It</p><p>explains the concept of insurance and how it is used to cover risk. How insurance is</p><p>transacted as a business and how the insurance market operates is also explained. The</p><p>relationship between insurers and their customers and the importance of insurance</p><p>contracts are discussed. Some commonly used insurance terms are also listed out. An</p><p>overview of major life insurance and general insurance products is included as well.</p><p> </p><p><strong>Course Outline</strong></p><p>Principles of Insurance</p><p>Practice of Life Insurance</p><p>Life Insurance Underwiting</p><p>Legal &amp; Regulatory Aspects for Life Underwriters</p><p>Fundamentals of Medical Underwriting </p>', NULL, '<p><br></p>', '<p><br></p>', 120000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 09:47:21', '2022-05-12 09:47:21', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661163130/Tmc%20institute/Underwriting-_Life-_-General_-for-Insurance_pa9w7o.png', 'Insurance Courses'),
(59, 'GOVERNANCE, RISK, COMPLIANCE & FINANCIAL CRIME PREVENTION TRAINING FOR LAW ENFORCEMENT OFFICER OR POLICE OFFICERS', 'g', '<p><strong>Course Overview</strong></p>\n<p>Day One</p>\n<p>1. Governance, Information and Enforcement officers Or Police Officers</p>\n<p>• Introduction</p><p>• Researching Information and Intelligence Processes </p>\n<p>• Contemporary Governance</p><p>• Governance and Markets</p>\n<p>• Analytical Framework: An Intelligence Processes</p><p>• Documentation/Data Management Security</p><p>• Conclusion</p><p><br></p><p>Day Two</p><p>2. Producing and Using Artificial Intelligence </p><p>• Introduction </p><p>• Organising Analysis</p><p>• Tactical And Strategic Analysis For Financial Crime Enforcement Officers  </p><p>• The Role Of Financial Crime Analysts And Investigators Officers</p><p>• Analytical Methods</p><p>• Is Intelligence-Led Policing Making A Difference?</p><p>• Organising Dissemination</p><p>• Dissemination And Action</p><p>• Public-Private Sharing</p><p>• Conclusion </p><p><br></p><p>Day Three</p><p>3. Development of Intelligence-Led Policing in the UK</p><p>• Introduction</p><p>• The Search For New Police Strategies</p><p>• Organising Intelligence</p><p>• The Organisation of Criminal Intelligence</p><p>1. Local level</p><p>2. Force Level</p><p>3. Regional Level</p><p>4. National Level</p><p>5. Customs and Excise</p><p>• Northern Ireland</p><p>• Reporting Writing and Templates</p><p>• Reporting in Security</p><p>• Conclusion</p>', NULL, '<p><br></p>', '<p><br></p>', 150000.00, 'naira', NULL, NULL, '3 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 09:50:30', '2022-05-12 09:50:30', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661161288/Tmc%20institute/GOVERNANCE_-RISK_-COMPLIANCE-_-FINANCIAL-CRIME-PREVENTION-TRAINING-FOR-LAW-ENFORCEMENT-OFFICER-OR-POLICE-OFFICERS_budxw1.png', 'Law Enforcement Officers Course'),
(60, 'Leadership, Public Safety & Security', 'l', '<p><strong>Course Overview</strong></p>\n<p>Day One</p><p>1.1 Policing Networks</p>\n<p>• Introduction</p>\n<p>• Policing In Canada, United Kingdom and United States Networks and Policing</p>\n<p>• The Three-dimensional Explanation of Policing Networks</p><p>• Transnational Policing Network</p><p>• Rounding Up The Usual Suspects</p><p>1.2 Targeting The Intelligence Process</p><p>• Introduction</p><p>• Setting Priorities</p><p>• Targeting in Canada, UK and US</p><p>• Intelligence and growth of Informalism</p><p>• Conclusion</p><p><br></p><p>Day Two</p><p>2. Crime Networks</p><p>• Introduction</p><p>• Organised Crime</p><p>• Criminal Organisations</p><p>• Criminal Markets</p><p>• Criminal Governments</p><p>• Traditional Organised Crime</p><p>• Election Sentiment Analysis</p><p>• Elections experience from countries that have demonstrated or conducted credible elections</p><p>• Conclusion</p><p><br></p><p>Day Three</p><p>3. Technologies Of Information Gathering</p><p>• Introduction</p><p>• Documentation Styles</p><p>• The Legal Context</p><p>• The Knowledge Store</p><p>• Open Sources</p><p>• Covert Technical Surveillance</p><p>• Conclusion</p>', NULL, '<p><br></p>', '<p><br></p>', 150000.00, 'naira', NULL, NULL, '3 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 09:54:37', '2022-05-12 09:54:37', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661161561/Tmc%20institute/Leadership_-Public-Safety-_-Security_sehwbk.png', 'Law Enforcement Officers Courses'),
(61, 'Ethical Corporate Culture', 'e', '<p><strong>Course Overview</strong></p>\n<p>The course is intended to educate learners about corporate cultures and the importance of following ethics in a corporate environment. \nThe course topics include introduction to corporate cultures, how to develop ethics in a corporate, benefits of ethical culture, \nhow to sustain the ethical environment and impact on business due to unethical behaviour.</p><p><br></p><p>Module 1: Corporate Culture</p>\n<p>• Vision and Mission</p><p>• Organizational structure</p><p>• Formal policies</p><p><br></p><p>Module 2: Ethics and its importance</p><p>• Values</p><p>• Code of conduct</p><p>• Brand image</p><p><br></p><p>Module 3: Develop Ethical Corporate Culture</p><p>• Leadership to guide by example</p><p>• Communication of values</p><p>• Training programs</p><p><br></p><p>Module 4: Sustain Ethical Corporate Culture</p><p>• Policies for protection</p><p>• Neutral committee to resolve disputes</p><p><br></p><p>Module 5: Downfall with unethical culture</p><p>• Loss of respect</p><p>• Talent retention</p><p>• Customer loyalty</p><p><br></p>', NULL, '<p><br></p>', '<p><br></p>', 100000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 09:58:10', '2022-05-12 09:58:10', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661161129/Tmc%20institute/Ethical-Corporate-Culture_udmetm.png', 'Corporate Governance'),
(62, 'Strategic Risk Management for Senior Management', 's', '<p><strong>Course Overview</strong></p>\n<p>The course is intended to educate learners on roles and responsibilities of senior management in policy making for risk management.</p>\n<p>Module 1: What is strategic risk?</p><p>• Definition</p><p>• Purpose</p><p>Module 2: Boards and CEOs driving strategic risk management</p><p>• Policies</p><p>• Responsibilities</p><p>Module 3: The Strategic Risk Assessment Process</p><p>• Achieve a deep understanding of the strategy of the organization</p><p>• Gather views and data on strategic risks</p><p>• Prepare a preliminary strategic risk profile</p><p>• Validate and finalize the strategic risk profile</p><p>• Communicate the strategic risk profile and strategic risk management action plan</p><p>• Implement the strategic risk management action plan</p><p>Module 4: How to Manage Strategic Risk for Organizational Success?</p><p>• Budget allocation for strategic assets</p><p>• Dedicated experts to manage risks</p><p><br></p>', NULL, '<p><br></p>', '<p><br></p>', 100000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 10:00:24', '2022-05-12 10:00:24', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661162866/Tmc%20institute/Strategic-Risk-Management-for-Senior-Management_smwamc.png', 'Risk Management Courses'),
(63, 'The Impact of Technology on GRC', 'i', '<p><strong>Course Overview</strong></p>\n<p>The course is intended to educate learners about fundamentals, framework and technological benefits in GRC.</p>\n<p>Module 1: The basic concept of GRC</p><p>Module 2: Key components of effective GRC programme</p>\n<p>• Leadership</p><p>• Risk assessment</p>\n<p>• Standards and controls</p><p>• Monitoring and response</p><p>• Training and awareness</p><p>Module 3: The Role of Technology in GRC</p><p>• Identity management</p><p>• Access management</p><p>• Automation of certification</p><p>• Reconciliation</p><p>Module 4: Benefits of Technology in GRC</p><p>• Analytics Driving Real Time and Accurate GRC Decision Making</p><p>• Better ROI for the Business</p><p>Module 5: Complications of integrating Technology in GRC</p>\n<p>• Mapping the processes accurately</p><p>• Defining the roles</p><p>• Effective communication</p><p><br></p>', NULL, '<p><br></p>', '<p><br></p>', 100000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 10:04:58', '2022-05-12 10:04:58', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661163064/Tmc%20institute/The-Impact-of-Technology-on-GRC_l2owjt.png', 'GRC'),
(64, 'Building and Leading Change In GRC', 'b', '<p><strong>Course Overview</strong></p>\n<p>The course is intended to educate learners, who are already aware of fundamentals of GRC, about methodology to formulate and implement a change in GRC</p>\n<p><br></p><p>Module 1: Reasons for bringing change in GRC</p><p>• Too much of control</p><p>• Complicated rules</p><p>• Mergers and acquisitions</p><p><br></p><p>Module 2: Building the change management for GRC</p><p>• Clearly document expected outcomes</p><p>• Impacted parts of the organization</p><p><br></p><p>Module 3: Characteristics of effective GRC leadership</p><p>• Subject Matter experts to design simplified process</p><p>• Leaders with proper exposure and training</p><p><br></p><p>Module 4: Monitoring for success</p><p>• Tracking the impact</p>\n<p>• Effect on the other departments</p><p>• Proper communication</p><p><br></p><p>Module 5: Drawbacks of changing established GRC</p><p><br></p>', NULL, '<p><br></p>', '<p><br></p>', 100000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 10:07:30', '2022-05-12 10:07:30', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661160406/Tmc%20institute/Building-and-Leading-Change-In-GRC_gnuzrs.png', 'GRC'),
(65, 'Changing expectations: governance, conduct and outcomes', 'c', '<p><strong>Course Overview</strong></p>\n<p>The course is intended to educate learners about fundamentals of board expectations and dynamics of anticipated outcomes.</p>\n<p>Module 1: Introduction to Board</p><p>• Structure</p><p>• Responsibilities</p><p>Module 2: Fundamentals of governance</p><p>• Purpose of Board</p><p>• Importance of governance</p><p>• Framework of good governance</p><p>Module 3: Fundamentals of Code of Conduct</p><p>• Definition</p><p>• Outline</p><p>• Relation with Organizational Culture and reputation</p><p>Module 4: Outcomes</p><p>• Ownership</p>\n<p>• Transparency</p><p>• Ethics</p><p>Module 5: Changing expectations</p><p>• Legal Compliance</p><p>• Knowledge of duties</p><p><br></p>', NULL, '<p><br></p>', '<p><br></p>', 100000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 10:09:53', '2022-05-12 10:09:53', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661160484/Tmc%20institute/Changing-expectations--governance_-conduct-and-outcomes_ievkxz.png', 'Corporate Governance'),
(66, 'Financial Crime Compliance', 'f', '<p><strong>Course Overview</strong></p>\n<p>The course is intended to educate learners about financial ethics, corruption and management expectations on compliance.</p>\n<p>Module 1: What are financial crimes?</p><p>• Internal and external threats</p><p>• Market abuse and insider dealing.</p><p>• Electronic crime</p><p>• Money laundering</p><p><br></p><p>Module 2: Compliance policies and procedures</p><p>• Customer due diligence</p><p>• Maintain good records</p><p>• Suspicion reporting</p><p><br></p><p>Module 3: Audits and Risk identifications</p><p>• Responsibilities</p>\n<p>• Reporting obligations</p><p><br></p><p>Module 4: Compliance failure and consequences</p><p>• Code of conduct</p><p><br></p><p><br></p>\n\n<p><strong>Course Overview</strong></p>\n<p>The course is intended to educate learners about financial ethics, corruption and management expectations on compliance.</p>\n<p>Module 1: What are financial crimes?</p><p>• Internal and external threats</p><p>• Market abuse and insider dealing.</p><p>• Electronic crime</p><p>• Money laundering</p><p><br></p><p>Module 2: Compliance policies and procedures</p><p>• Customer due diligence</p><p>• Maintain good records</p><p>• Suspicion reporting</p><p><br></p><p>Module 3: Audits and Risk identifications</p><p>• Responsibilities</p>\n<p>• Reporting obligations</p><p><br></p><p>Module 4: Compliance failure and consequences</p><p>• Code of conduct</p><p><br></p><p><br></p>\n\n\n\n<p><strong>Course Overview</strong></p>\n<p>The course is intended to educate learners about financial ethics, corruption and management expectations on compliance.</p>\n<p>Module 1: What are financial crimes?</p><p>• Internal and external threats</p><p>• Market abuse and insider dealing.</p><p>• Electronic crime</p><p>• Money laundering</p><p><br></p><p>Module 2: Compliance policies and procedures</p><p>• Customer due diligence</p><p>• Maintain good records</p><p>• Suspicion reporting</p><p><br></p><p>Module 3: Audits and Risk identifications</p><p>• Responsibilities</p>\n<p>• Reporting obligations</p><p><br></p><p>Module 4: Compliance failure and consequences</p><p>• Code of conduct</p><p><br></p><p><br></p>\n\n\n', NULL, '<p><br></p>', '<p><br></p>', 100000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 10:12:34', '2022-05-12 10:12:34', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661161242/Tmc%20institute/Financial-Crime-Compliance_zaiir4.png', 'Financial Crime Prevention'),
(67, 'The Effective Use of Information Technology in Financial Crime Compliance (FCC)', 't', '<p><strong>Course Overview</strong></p>\n<p>The course is intended to educate learners about role of advanced information technology in avoidance, early detection and investigation of financial crimes.</p><p>Module 1: IT tools for regulating and controlling Financial crimes</p><p>• Graph Analytics</p><p>• False Positives and Costs</p><p><br></p><p>Module 2: Sophisticated frauds demand greater accuracy and efficiency</p><p>• AI, machine learning and big data</p><p>• Shared database of ‘bad actors’</p><p>• The connected defense</p><p><br></p><p>Module 3: Public-private partnership controls</p><p>• Quick access to intelligence</p><p>• Check on Cyber threats</p><p>• Common pool of Experts</p><p><br></p><p>Module 4: Constraints in deploying IT tools</p><p>• Training of leadership</p><p>• Hardware upgrades</p><p>• Budget approvals</p><p><br></p><p><br></p>', NULL, '<p><br></p>', '<p><br></p>', 100000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 10:14:56', '2022-05-12 10:14:56', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661162968/Tmc%20institute/The-Effective-Use-of-Information-Technology-in-Financial-Crime-Compliance-_FCC_vcpsdc.png', 'Financial Crime Prevention'),
(68, 'De-risking - Linking Risk Assessment to Business Strategy', 'd', '<p><strong>Course Overview</strong></p>\n<p>The course is intended to educate learners on need for a tighter alignment of risk management, performance and strategy of the organization.</p><p><br></p><p>Module 1: Steps to integrate Risk assessment with Business Strategy</p><p><br></p><p>Module 2: Step I - Strategic objectives breakdown</p><p>• Level 1: The Corporate Level</p><p>• Level 2: The Business Unit Level</p><p>• Level 3: The Functional Level</p><p><br></p><p>Module 3: Step II - Identifying factors associated with uncertainty</p><p>• Assumptions ranked with degree of risk</p><p><br></p><p>Module 4: Step III - Risk analysis</p><p>• IT models to predict results</p><p>• Audit of simulation</p><p><br></p><p>Module 5: Step IV - Turning risk analysis into actions</p><p>• Develop the policies</p><p>• Communicate the strategies</p><p>• Track the results with Auditors and experts</p><p><br></p>', NULL, '<p><br></p>', '<p><br></p>', 100000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 10:17:35', '2022-05-12 10:17:35', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1661161046/Tmc%20institute/De-risking---Linking-Risk-Assessment-to-Business-Strategy_zhykmx.png', 'Corporate Governance'),
(69, 'Cyber Security: Threats to the Organisation', 'c', '<p><strong>Course Overview</strong></p>\n<p>The course is intended to educate learners on fundamentals, framework and implementation of cyber security policies and infrastructure in organization.</p><p>Module 1: Introduction to Cyber Security </p><p>• Definition</p><p>• Purpose </p><p>• Rise in incidents </p><p>• Loss to organizations </p><p><br></p><p>Module 2: Types of Cyber Threats </p><p>• Ransomware</p><p>• Phishing</p><p>• Data leakage</p><p>• Hacking</p><p>• Insider threat</p><p><br></p><p>Module 3: Cyber Security Policies and Strategies </p><p>• Information Security Principle </p><p>• Firewall</p><p>• Controlled access </p><p>• Secured Infrastructure </p><p>• Software management Policy </p><p><br></p><p>Module 4: Detection and Reporting of a security violation</p><p>• AI and smart algorithms for early detection </p><p>• incident response and handling methodologies</p><p>• Legal response </p>', NULL, '<p><br></p>', '<p><br></p>', 100000.00, 'naira', NULL, NULL, '2 days', NULL, NULL, 1, 'English', 'lifetime', '2022-05-12 10:22:53', '2022-05-12 10:22:53', 'https://res.cloudinary.com/the-morgans-consortium/image/upload/v1652449149/Tmc%20institute/Cyber-Security-Professional-Training-for-beginners_uk2jea.png', 'Risk Management Courses');

